import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const invoicesRes = await api.get("/invoices");
        const leadsRes = await api.get("/leads");

        const customers = customersRes.data.items || [];
        const invoices = invoicesRes.data.items || [];
        const leads = leadsRes.data.items || [];

        const totalRevenue = invoices.reduce(
          (sum, inv) => sum + (inv.amount || 0),
          0
        );
        const paidInvoices = invoices.filter((inv) => inv.status === "Paid").length;
        const pendingInvoices = invoices.filter(
          (inv) => inv.status === "Pending"
        ).length;
        const inProgressLeads = leads.filter((l) => l.status === "New").length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads,
          totalRevenue,
          paidInvoices,
          pendingInvoices,
        });
      } catch (err) {
        console.error("Dashboard fetch error:", err.response?.data || err.message);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />
        <main className="p-6 overflow-auto">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Dashboard</h2>

          {/* Row 1 - Customers & Leads */}
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-md p-6 w-full border-l-4 border-blue-500">
              <h3 className="text-lg font-semibold text-gray-700">Customers</h3>
              <p className="text-3xl font-bold text-blue-600 mt-2">
                {stats.customers}
              </p>
            </div>

            <div className="bg-white rounded-lg shadow-md p-6 w-full border-l-4 border-yellow-500">
              <h3 className="text-lg font-semibold text-gray-700">Leads</h3>
              <p className="text-3xl font-bold text-yellow-600 mt-2">
                {stats.leads}
              </p>
              <p className="text-sm text-gray-500 mt-1">
                In Progress:{" "}
                <span className="font-semibold text-purple-600">
                  {stats.inProgressLeads}
                </span>
              </p>
            </div>

            {/* Row 2 - Invoice Stats */}
            <div className="bg-white rounded-lg shadow-md p-6 w-full border-l-4 border-green-500">
              <h3 className="text-lg font-semibold text-gray-700">
                Invoice Summary
              </h3>
              <div className="flex flex-col sm:flex-row sm:justify-between mt-4">
                <p className="text-md text-gray-600">
                  Paid:{" "}
                  <span className="font-bold text-emerald-600">
                    {stats.paidInvoices}
                  </span>
                </p>
                <p className="text-md text-gray-600">
                  Pending:{" "}
                  <span className="font-bold text-rose-600">
                    {stats.pendingInvoices}
                  </span>
                </p>
              </div>
            </div>

            {/* Big Card - Total Invoices + Revenue */}
            <div className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white rounded-xl shadow-lg p-8 w-full">
              <h3 className="text-2xl font-semibold">Total Invoices</h3>
              <p className="text-5xl font-bold mt-3">{stats.invoices}</p>
              <p className="mt-4 text-lg">
                Total Revenue:{" "}
                <span className="font-bold">₹{stats.totalRevenue}</span>
              </p>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
