import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    newLeads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    pendingRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // ✅ fetch customers
        const customersRes = await api.get("/customers");
        const customers =
          customersRes.data.items ||
          customersRes.data.customers ||
          customersRes.data ||
          [];

        // ✅ fetch invoices
        const invoicesRes = await api.get("/invoices");
        let invoices =
          invoicesRes.data.items ||
          invoicesRes.data.invoices ||
          invoicesRes.data ||
          [];

        console.log("Invoices API data:", invoices);

        // ✅ filter for normal users if matching field exists
        if (user?.role === "user") {
          invoices = invoices.filter(
            (inv) =>
              inv.createdBy === user._id ||
              inv.userId === user._id ||
              inv.owner === user._id
          );
        }

        // ✅ fetch leads
        const leadsRes = await api.get("/leads");
        const leads =
          leadsRes.data.items ||
          leadsRes.data.leads ||
          leadsRes.data ||
          [];

        // ✅ revenue calculations
        const paidInvoicesList = invoices.filter(
          (inv) =>
            inv.status?.toLowerCase() === "paid" ||
            inv.status?.toLowerCase() === "success"
        );
        const pendingInvoicesList = invoices.filter(
          (inv) =>
            inv.status?.toLowerCase() === "pending" ||
            inv.status?.toLowerCase() === "unpaid"
        );

        const totalRevenue = paidInvoicesList.reduce(
          (sum, inv) => sum + (Number(inv.amount || inv.total || 0)),
          0
        );

        const pendingRevenue = pendingInvoicesList.reduce(
          (sum, inv) => sum + (Number(inv.amount || inv.total || 0)),
          0
        );

        // ✅ leads calculations
        const convertedLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "converted"
        ).length;
        const newLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "new"
        ).length;
        const inProgressLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "in progress"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: convertedLeads,
          newLeads,
          inProgressLeads,
          totalRevenue,
          pendingRevenue,
          paidInvoices: paidInvoicesList.length,
          pendingInvoices: pendingInvoicesList.length,
        });
      } catch (err) {
        console.error(
          "Dashboard fetch error:",
          err.response?.data || err.message
        );
      }
    };

    fetchStats();
  }, [user]);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Navbar />
        <main className="p-6">
          {/* 🔹 Top Section: Customers & Leads */}
          <div className="flex flex-col md:flex-row md:flex-wrap gap-6 mb-8">
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-blue-600">
              <p className="text-gray-600 font-medium text-lg">Customers</p>
              <p className="text-3xl font-bold text-blue-700 mt-2">
                {stats.customers}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-purple-600">
              <p className="text-gray-600 font-medium text-lg">
                Leads (Converted)
              </p>
              <p className="text-3xl font-bold text-purple-700 mt-2">
                {stats.leads}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-orange-600">
              <p className="text-gray-600 font-medium text-lg">New Leads</p>
              <p className="text-3xl font-bold text-orange-700 mt-2">
                {stats.newLeads}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-indigo-600">
              <p className="text-gray-600 font-medium text-lg">
                Leads In Progress
              </p>
              <p className="text-3xl font-bold text-indigo-700 mt-2">
                {stats.inProgressLeads}
              </p>
            </div>
          </div>

          {/* 🔹 Bottom Section: Invoices & Revenue */}
          <div className="flex flex-col md:flex-row md:flex-wrap gap-6">
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-green-600">
              <p className="text-gray-600 font-medium text-lg">Invoices</p>
              <p className="text-3xl font-bold text-green-700 mt-2">
                {stats.invoices}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-teal-600">
              <p className="text-gray-600 font-medium text-lg">
                Total Revenue (Paid)
              </p>
              <p className="text-3xl font-bold text-teal-700 mt-2">
                ₹{stats.totalRevenue}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-amber-600">
              <p className="text-gray-600 font-medium text-lg">
                Pending Revenue
              </p>
              <p className="text-3xl font-bold text-amber-700 mt-2">
                ₹{stats.pendingRevenue}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-emerald-600">
              <p className="text-gray-600 font-medium text-lg">Paid Invoices</p>
              <p className="text-3xl font-bold text-emerald-700 mt-2">
                {stats.paidInvoices}
              </p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-rose-600">
              <p className="text-gray-600 font-medium text-lg">
                Pending Invoices
              </p>
              <p className="text-3xl font-bold text-rose-700 mt-2">
                {stats.pendingInvoices}
              </p>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
