import { NavLink } from "react-router-dom";
import {
  LayoutDashboard,
  Users,
  FileText,
  BarChart,
  HelpCircle,
  Settings,
} from "lucide-react";

const Sidebar = () => {
  const navItems = [
    { name: "Dashboard", path: "/", icon: LayoutDashboard },
    { name: "Customers", path: "/customers", icon: Users },
    { name: "Invoices", path: "/invoices", icon: FileText },
    { name: "Reports", path: "/reports", icon: BarChart },
  ];

  return (
    <aside className="w-64 h-screen bg-white shadow-md flex flex-col justify-between">
      {/* Top Section */}
      <div>
        {/* Logo */}
        <div className="h-16 flex items-center justify-center border-b">
          <h1 className="text-xl font-bold text-teal-700 tracking-tight">
            MyCRM
          </h1>
        </div>

        {/* Navigation */}
        <nav className="mt-4 px-2">
          <ul className="space-y-1">
            {navItems.map((item) => {
              const Icon = item.icon;
              return (
                <li key={item.name}>
                  <NavLink
                    to={item.path}
                    className={({ isActive }) =>
                      `flex items-center gap-3 px-4 py-2 rounded-lg text-gray-700 hover:bg-teal-50 hover:text-teal-700 transition ${
                        isActive ? "bg-teal-100 text-teal-700 font-medium" : ""
                      }`
                    }
                  >
                    <Icon size={20} />
                    <span>{item.name}</span>
                  </NavLink>
                </li>
              );
            })}
          </ul>
        </nav>
      </div>

      {/* Footer Section */}
      <div className="border-t px-2 py-3">
        <NavLink
          to="/settings"
          className="flex items-center gap-3 px-4 py-2 rounded-lg text-gray-600 hover:bg-gray-100 transition"
        >
          <Settings size={20} />
          <span>Settings</span>
        </NavLink>
        <NavLink
          to="/help"
          className="flex items-center gap-3 px-4 py-2 rounded-lg text-gray-600 hover:bg-gray-100 transition"
        >
          <HelpCircle size={20} />
          <span>Help</span>
        </NavLink>
      </div>
    </aside>
  );
};

export default Sidebar;
