import { useContext } from "react";
import { AuthContext } from "../Context/AuthContext";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ title }) => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <header className="h-16 bg-white shadow flex items-center justify-between px-6">
      {/* Left: Brand / Logo */}
      <div className="flex items-center gap-2">
        <img src="/logo192.png" alt="Logo" className="w-8 h-8" />
        <h1 className="text-lg font-semibold text-teal-700">{title || "Flow CRM"}</h1>
      </div>

      {/* Middle: Navigation Links */}
      <nav className="hidden md:flex items-center gap-6">
        <Link to="/" className="text-gray-700 hover:text-teal-600 transition">
          Home
        </Link>
        <Link to="/about" className="text-gray-700 hover:text-teal-600 transition">
          About
        </Link>
        <Link to="/contact" className="text-gray-700 hover:text-teal-600 transition">
          Contact
        </Link>
      </nav>

      {/* Right: User Actions */}
      <div>
        {user ? (
          <div className="flex items-center gap-3">
            <img
              src={user.avatar || "/path/to/default-avatar.png"}
              alt="User Avatar"
              className="w-8 h-8 rounded-full"
            />
            <span className="text-gray-700 font-medium">{user.name}</span>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600 transition"
            >
              Logout
            </button>
          </div>
        ) : (
          <Link
            to="/login"
            className="bg-teal-600 text-white py-1 px-4 rounded hover:bg-teal-700 transition"
          >
            Login
          </Link>
        )}
      </div>
    </header>
  );
};

export default Navbar;
