import { useContext, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { AuthContext } from "../Context/AuthContext";

// Optional: you can use icons from react-icons if installed
// import { FaUsers, FaFileInvoice, FaChartBar, FaUser } from "react-icons/fa";

const Sidebar = () => {
  const { user, logout } = useContext(AuthContext);
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);

  // Menu structure
  const menuItems = [
    { name: "Dashboard", path: user.role === "admin" ? "/admin" : "/user" /* , icon: <FaChartBar /> */ },
    { name: "Customers", path: "/customers" /* , icon: <FaUsers /> */ },
    { name: "Invoices", path: "/invoices" /* , icon: <FaFileInvoice /> */ },
    { name: "Leads", path: "/leads" /* , icon: <FaUsers /> */ },
    ...(user.role === "admin"
      ? [
          { name: "Users", path: "/admin/users" /* , icon: <FaUser /> */ },
          { name: "Reports", path: "/admin/reports" /* , icon: <FaChartBar /> */ },
        ]
      : []),
  ];

  return (
    <div className={`bg-gray-800 text-white min-h-screen transition-all duration-300 ${collapsed ? "w-20" : "w-64"} flex flex-col`}>
      {/* Logo / Title */}
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        {!collapsed && <h1 className="text-xl font-bold">MyApp</h1>}
        <button
          onClick={() => setCollapsed(!collapsed)}
          className="text-gray-400 hover:text-white text-lg"
        >
          {collapsed ? "➤" : "⬅"}
        </button>
      </div>

      {/* Menu Items */}
      <nav className="flex-1 mt-6">
        <ul className="space-y-2">
          {menuItems.map((item) => (
            <li key={item.name}>
              <Link
                to={item.path}
                className={`flex items-center gap-3 p-3 rounded hover:bg-gray-700 transition-colors ${
                  location.pathname === item.path ? "bg-gray-700 font-semibold" : ""
                }`}
              >
                {/* Uncomment icons if react-icons installed */}
                {/* <span>{item.icon}</span> */}
                {!collapsed && <span>{item.name}</span>}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {/* Logout Button */}
      <div className="p-4 border-t border-gray-700">
        <button
          onClick={logout}
          className="w-full bg-red-600 hover:bg-red-700 py-2 rounded text-white font-semibold transition"
        >
          {collapsed ? "⏻" : "Logout"}
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
