import { useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import { AuthContext } from "../Context/AuthContext";

const Sidebar = () => {
  const { user, logout } = useContext(AuthContext);
  const location = useLocation();

  const menuItems = [
    { name: "Dashboard", path: user.role === "admin" ? "/admin" : "/user" },
    { name: "Customers", path: "/customers" },
    { name: "Invoices", path: "/invoices" },
    { name: "Leads", path: "/leads" },
    ...(user.role === "admin"
      ? [
          { name: "Users", path: "/admin/users" },
          { name: "Reports", path: "/admin/reports" },
        ]
      : []),
  ];

  return (
    <div className="bg-white shadow h-screen w-64 flex flex-col">
      {/* Logo / Title */}
      <div className="p-6 border-b border-gray-200">
        <h1 className="text-2xl font-bold text-gray-800">Dashboard</h1>
      </div>

      {/* Menu */}
      <nav className="flex-1 mt-4">
        <ul className="space-y-2">
          {menuItems.map((item) => (
            <li key={item.name}>
              <Link
                to={item.path}
                className={`block px-6 py-3 rounded hover:bg-gray-100 transition ${
                  location.pathname === item.path ? "bg-gray-100 font-semibold" : "text-gray-700"
                }`}
              >
                {item.name}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {/* Logout */}
      <div className="p-6 border-t border-gray-200">
        <button
          onClick={logout}
          className="w-full bg-red-600 hover:bg-red-700 py-2 rounded text-white font-semibold transition"
        >
          Logout
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
