import { Link, useLocation } from "react-router-dom";
import { LayoutDashboard, Users, FileText, Contact, Settings, LogOut, Moon, Sun } from "lucide-react";
import React from "react";
// Assume you import Avatar component or use image

const navItems = [
  { name: "Dashboard", path: "/", icon: <LayoutDashboard size={20} /> },
  { name: "Customers", path: "/customers", icon: <Users size={20} /> },
  { name: "Invoices", path: "/invoices", icon: <FileText size={20} /> },
  { name: "Leads", path: "/leads", icon: <Contact size={20} /> },
];

const Sidebar = () => {
  const location = useLocation();
  const [collapsed, setCollapsed] = React.useState(false);
  const [darkMode, setDarkMode] = React.useState(false);

  return (
    <aside className={`h-screen bg-white ${darkMode ? 'bg-gray-800 text-gray-200' : 'bg-white text-gray-700'} shadow-sm flex flex-col transition-width duration-300 ${collapsed ? 'w-20' : 'w-64'}`}>
      
      {/* Top / Brand + Collapse Button */}
      <div className="flex items-center justify-between p-4 border-b">
        {!collapsed && <h1 className="text-xl font-bold text-teal-700">Flow CRM</h1>}
        <button
          onClick={()=>setCollapsed(!collapsed)}
          className="p-2 rounded hover:bg-gray-200 dark:hover:bg-gray-700 transition"
          title={collapsed ? "Expand sidebar" : "Collapse sidebar"}
        >
          {/* icon to indicate collapse/expand */}
          {collapsed ? '→' : '←'}
        </button>
      </div>

      {/* User Profile */}
      <div className="flex items-center gap-3 p-4">
        <img src="/path/to/avatar.jpg" alt="User Avatar" className="w-10 h-10 rounded-full" />
        {!collapsed && <div>
          <p className="font-medium">John Doe</p>
          <p className="text-sm text-gray-500 dark:text-gray-400">Admin</p>
        </div>}
      </div>

      {/* Search */}
      {!collapsed && (
        <div className="px-4 py-2">
          <input
            type="text"
            placeholder="Search..."
            className="w-full px-3 py-2 bg-gray-100 dark:bg-gray-700 rounded focus:outline-none focus:ring focus:ring-teal-300 dark:focus:ring-teal-600 transition"
          />
        </div>
      )}

      {/* Navigation links */}
      <nav className="flex-1 p-4 space-y-1 overflow-y-auto">
        {navItems.map(item => {
          const active = location.pathname === item.path;
          return (
            <Link
              key={item.name}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-2 rounded-lg transition-all duration-200
                ${active
                  ? "bg-teal-50 text-teal-700 font-medium shadow-sm"
                  : "hover:bg-gray-100 dark:hover:bg-gray-700 hover:text-teal-600"}
                ${collapsed ? 'justify-center' : ''}
              `}
            >
              {item.icon}
              {!collapsed && <span>{item.name}</span>}
            </Link>
          )
        })}
      </nav>

      {/* Bottom / Utility Links + Theme Toggle */}
      <div className="p-4 border-t space-y-2">
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="flex items-center gap-3 w-full px-4 py-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition"
        >
          {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          {!collapsed && <span>{darkMode ? "Light Mode" : "Dark Mode"}</span>}
        </button>

        <Link
          to="/settings"
          className="flex items-center gap-3 px-4 py-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition"
        >
          <Settings size={20} />
          {!collapsed && <span>Settings</span>}
        </Link>

        <Link
          to="/logout"
          className="flex items-center gap-3 px-4 py-2 text-red-600 rounded-lg hover:bg-red-50 dark:hover:bg-red-700 transition"
        >
          <LogOut size={20} />
          {!collapsed && <span>Logout</span>}
        </Link>
      </div>
    </aside>
  );
}

export default Sidebar;
