import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function EditInvoice() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    customer: "",
    amount: "",
    status: "",
    dueDate: "",
  });

  useEffect(() => {
    axios.get(`http://localhost:5000/api/invoices/${id}`, {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    })
    .then(res => {
      // format date if needed
      setFormData({
        customer: res.data.customer || "",
        amount: res.data.amount || "",
        status: res.data.status || "",
        dueDate: res.data.dueDate ? res.data.dueDate.slice(0,10) : "",
      });
    })
    .catch(err => console.error(err));
  }, [id]);

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/api/invoices/${id}`, formData, {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    })
    .then(() => navigate("/invoices"))
    .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>Edit Invoice</h2>
      <form onSubmit={handleSubmit}>
        <input name="customer" value={formData.customer} onChange={handleChange} placeholder="Customer" />
        <input name="amount" value={formData.amount} onChange={handleChange} placeholder="Amount" />
        <input name="status" value={formData.status} onChange={handleChange} placeholder="Status" />
        <input type="date" name="dueDate" value={formData.dueDate} onChange={handleChange} />
        <button type="submit">Update</button>
      </form>
    </div>
  );
}
