import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";

const Invoices = () => {
  const navigate = useNavigate();
  const [invoices, setInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const res = await api.get("/invoices");
        const items = res.data.items ?? res.data ?? [];
        setInvoices(items);
      } catch (err) {
        console.error(err.response?.data || err.message);
        alert("Could not load invoices");
      } finally {
        setLoading(false);
      }
    };
    fetchInvoices();
  }, []);

  const handleDelete = async (id) => {
    const ok = window.confirm("Delete this invoice? This cannot be undone.");
    if (!ok) return;

    try {
      await api.delete(`/invoices/${id}`);
      setInvoices((prev) => prev.filter((i) => i._id !== id));
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Delete Failed");
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />
        <main className="p-6 overflow-auto">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">Invoices</h1>
            <button
              onClick={() => navigate("/invoices/add")}
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
            >
              Add Invoice
            </button>
          </div>

          {loading ? (
            <p className="text-gray-500">Loading Invoices...</p>
          ) : invoices.length === 0 ? (
            <p className="text-gray-500">No Invoices Found.</p>
          ) : (
            <div className="overflow-auto rounded-lg border border-gray-200 shadow-sm">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-4 py-2 text-left">Invoice ID</th>
                    <th className="px-4 py-2 text-left">Customer</th>
                    <th className="px-4 py-2 text-left">Amount</th>
                    <th className="px-4 py-2 text-left">Status</th>
                    <th className="px-4 py-2 text-left">Created At</th>
                    <th className="px-4 py-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {invoices.map((inv) => (
                    <tr key={inv._id}>
                      <td className="px-4 py-2">{inv._id.slice(-6)}</td>
                      <td className="px-4 py-2">{inv.customer?.name ?? inv.customer}</td>
                      <td className="px-4 py-2">{inv.amount}</td>
                      <td className="px-4 py-2">{inv.status ?? "Pending"}</td>
                      <td className="px-4 py-2">{new Date(inv.createdAt).toLocaleDateString()}</td>
                      <td className="px-4 py-2 space-x-2">
                        <button
                          onClick={() => navigate(`/invoices/edit/${inv._id}`)}
                          className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600 transition"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete(inv._id)}
                          className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default Invoices;
