import { Link, useLocation } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../Context/AuthContext";
import { FaTachometerAlt, FaUsers, FaFileInvoiceDollar, FaUserTie, FaChartBar, FaUser, FaSignOutAlt } from "react-icons/fa";

const Sidebar = () => {
  const { user, logout } = useContext(AuthContext);
  const location = useLocation();

  const links = [
    { name: "Dashboard", path: user.role === "admin" ? "/admin" : "/user", icon: <FaTachometerAlt /> },
    { name: "Customers", path: "/customers", icon: <FaUser /> },
    { name: "Invoices", path: "/invoices", icon: <FaFileInvoiceDollar /> },
    { name: "Leads", path: "/leads", icon: <FaUserTie /> },
  ];

  if (user.role === "admin") {
    links.push({ name: "Users", path: "/admin/users", icon: <FaUsers /> });
    links.push({ name: "Reports", path: "/admin/reports", icon: <FaChartBar /> });
  }

  return (
    <div className="w-64 bg-gray-900 text-white flex flex-col h-screen p-6">
      <h2 className="text-2xl font-bold mb-10">Admin Panel</h2>
      <nav className="flex-1 flex flex-col gap-3">
        {links.map((link) => (
          <Link
            key={link.name}
            to={link.path}
            className={`flex items-center gap-3 p-3 rounded hover:bg-gray-700 transition ${
              location.pathname === link.path ? "bg-gray-700" : ""
            }`}
          >
            {link.icon} <span>{link.name}</span>
          </Link>
        ))}
      </nav>
      <button
        onClick={logout}
        className="mt-auto bg-red-600 p-3 rounded hover:bg-red-700 transition flex items-center justify-center gap-2"
      >
        <FaSignOutAlt /> Logout
      </button>
    </div>
  );
};

export default Sidebar;
