import axios from "axios"
import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

const Leads = ()=>{
    const [leads, setLeads] = useState([])

    useEffect(()=>{
        fetchLeads()
    }, [])

    const fetchLeads = async () => {
        try{
            const res = await axios.get("http://localhost:5000/api/leads")
            setLeads(res.data)
        }catch(err){
            console.error(err)
        }
    }

    const handleDelete = async(id)=>{
        try{
            await axios.delete(`http"//localhost:5000/api/leads/${id}`)
            setLeads(leads.filter((lead)=> lead._id !==id))
        }catch(err){
            console.error(err)
        }
    }
    return(
        <div className="bg-white shadow p-6 rounded-lg">
            <h1 className="text-xl font-bold mb-4">Leads</h1>
            <Link to = "/leads/add">Add New Lead</Link>
            <table className="w-full" border={1} cellPadding={10}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {leads.map((lead)=>(
                        <tr key={lead._id}>
                            <td>{lead.name}</td>
                            <td>{lead.email}</td>
                            <td>{lead.phone}</td>
                            <td>{lead.status}</td>
                            <td><Link to={`/leads/edit/${lead._id}`}>Edit</Link> {" | "} <button onClick={()=>handleDelete(lead._id)}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
export default Leads
