import { BrowserRouter as Router, Routes, Route, Navigate} from "react-router-dom";
import Sidebar from "./Components/Sidebar";
import Navbar from "./Components/Navbar";
import Dashboard from "./Pages/Dashboard";
import Customer from "./Pages/Customer";
import Invoices from "./Pages/Invoices";
import Login from "./Pages/Login";
import Signup from "./Pages/Signup";
import AddCust from "./Pages/AddCustomer";
import { useContext } from "react";
import { AuthContext } from "./Context/AuthContext";


function App() {
   const {user} = useContext(AuthContext)
   if(!user){
    return(
      <Routes>
          <Route path="/login" element ={<Login />} />
          <Route path="/signup" element = {<Signup />} /> 
          <Route path ="*" element = {<Navigate to={"/login"}/>} />
      </Routes>
    )
   }
  return (


        <div>    
           <div className="flex h-screen bg-gray-100">
             <Sidebar />

             <div className="flex-1 flex flex-col">
             <Navbar />

             <main className="p-6 flex-1 overflow-y-auto"></main>
           </div>
       </div>
    }/>
  <Routes>
    <Route path="/customers" element={<div className="flex h-screen bg-gray-100">
             <Sidebar />

             <div className="flex-1 flex flex-col">
             <Navbar />

             <main className="p-6 flex-1 overflow-y-auto"> <Customer /> </main>
           </div>
       </div>}/>
       <Route path="Invoices" element = {<div className="flex h-screen bg-gray-100">
             <Sidebar />

             <div className="flex-1 flex flex-col">
             <Navbar />

             <main className="p-6 flex-1 overflow-y-auto"> <Invoices /> </main>
           </div>
       </div>} />
       <Route path="/customers/add" element = {token? <AddCust />: <Navigate to={"/login"}/>} />
       </Routes>
       </div>

  );
}

export default App;
