import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api"; // your axios instance with baseURL
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch all data concurrently
        const [custRes, invRes, leadRes] = await Promise.all([
          api.get("/customers", { headers: { Authorization: `Bearer ${token}` } }),
          api.get("/invoices", { headers: { Authorization: `Bearer ${token}` } }),
          api.get("/leads", { headers: { Authorization: `Bearer ${token}` } }),
        ]);

        const customers = custRes.data.items || custRes.data || [];
        const invoices = invRes.data.items || invRes.data || [];
        const leads = leadRes.data.items || leadRes.data || [];

        const inProgress = leads.filter(
          (l) => l.status === "Contacted" || l.status === "Qualified"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads: inProgress,
        });
      } catch (err) {
        console.error("Error fetching dashboard stats:", err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <Navbar />

        {/* Dashboard Content */}
        <main className="flex-1 p-6 overflow-y-auto">
          <h1 className="text-3xl font-bold mb-6">Welcome, {user?.username}</h1>

          {loading ? (
            <p>Loading stats...</p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition">
                <h2 className="text-lg font-semibold text-gray-600">Customers</h2>
                <p className="text-3xl font-bold mt-2">{stats.customers}</p>
              </div>

              <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition">
                <h2 className="text-lg font-semibold text-gray-600">Invoices</h2>
                <p className="text-3xl font-bold mt-2">{stats.invoices}</p>
              </div>

              <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition">
                <h2 className="text-lg font-semibold text-gray-600">Leads</h2>
                <p className="text-3xl font-bold mt-2">{stats.leads}</p>
              </div>

              <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition">
                <h2 className="text-lg font-semibold text-gray-600">Leads In Progress</h2>
                <p className="text-3xl font-bold mt-2">{stats.inProgressLeads}</p>
              </div>
            </div>
          )}

          {/* Quick Action Buttons */}
          <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4">
            <button
              onClick={() => window.location.href = "/customers/add"}
              className="bg-blue-600 text-white p-4 rounded shadow hover:bg-blue-700 transition"
            >
              Add Customer
            </button>
            <button
              onClick={() => window.location.href = "/invoices/add"}
              className="bg-green-600 text-white p-4 rounded shadow hover:bg-green-700 transition"
            >
              Add Invoice
            </button>
            <button
              onClick={() => window.location.href = "/leads/add"}
              className="bg-yellow-500 text-white p-4 rounded shadow hover:bg-yellow-600 transition"
            >
              Add Lead
            </button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
