import { useEffect, useState } from "react";
import api from "../api"; // axios instance with baseURL
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";

const UserDashboard = () => {
  const [stats, setStats] = useState({
    customers: 0,
    leads: 0,
    invoices: 0,
    pendingInvoices: 0,
  });
  const [loading, setLoading] = useState(true);

  const fetchStats = async () => {
    try {
      setLoading(true);
      const [customersRes, leadsRes, invoicesRes] = await Promise.all([
        api.get("/customers"),
        api.get("/leads"),
        api.get("/invoices"),
      ]);

      const customers = customersRes.data.items?.length ?? customersRes.data.length ?? 0;
      const leads = leadsRes.data.items?.length ?? leadsRes.data.length ?? 0;
      const invoices = invoicesRes.data.items?.length ?? invoicesRes.data.length ?? 0;
      const pendingInvoices = invoicesRes.data.items?.filter(i => i.status === "Pending").length ?? 0;

      setStats({ customers, leads, invoices, pendingInvoices });
    } catch (err) {
      console.error("Error fetching dashboard stats:", err.response?.data || err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading dashboard...</p>;

  return (
    <div className="flex min-h-screen">
      <Sidebar />
      <div className="flex-1 p-6">
        <Navbar title="Dashboard" />

        <h1 className="text-2xl font-bold mb-6">Welcome to Your Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="p-4 bg-white rounded shadow text-center">
            <h2 className="text-lg font-semibold">Customers</h2>
            <p className="text-3xl font-bold">{stats.customers}</p>
          </div>
          <div className="p-4 bg-white rounded shadow text-center">
            <h2 className="text-lg font-semibold">Leads</h2>
            <p className="text-3xl font-bold">{stats.leads}</p>
          </div>
          <div className="p-4 bg-white rounded shadow text-center">
            <h2 className="text-lg font-semibold">Invoices</h2>
            <p className="text-3xl font-bold">{stats.invoices}</p>
          </div>
          <div className="p-4 bg-white rounded shadow text-center">
            <h2 className="text-lg font-semibold">Pending Invoices</h2>
            <p className="text-3xl font-bold">{stats.pendingInvoices}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
