// UserDashboard.jsx
import { useEffect, useState } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api"; // Axios instance
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, LineChart, Line, CartesianGrid, ResponsiveContainer } from "recharts";

const UserDashboard = () => {
  const [section, setSection] = useState("leads"); // "leads", "customers", "invoices"
  const [data, setData] = useState([]);
  const [stats, setStats] = useState({ totalLeads: 0, totalCustomers: 0, totalInvoices: 0 });

  // Fetch stats for top cards
  const fetchStats = async () => {
    try {
      const resLeads = await api.get("/leads");
      const resCustomers = await api.get("/customers");
      const resInvoices = await api.get("/invoices");

      setStats({
        totalLeads: resLeads.data.length,
        totalCustomers: resCustomers.data.length,
        totalInvoices: resInvoices.data.length,
      });
    } catch (err) {
      console.error("Error fetching stats:", err.response?.data || err.message);
    }
  };

  // Fetch chart data based on section
  const fetchChartData = async () => {
    try {
      if (section === "leads") {
        const res = await api.get("/leads");
        // Group by status
        const grouped = {};
        res.data.forEach((l) => (grouped[l.status] = (grouped[l.status] || 0) + 1));
        const chartData = Object.keys(grouped).map((k) => ({ name: k, count: grouped[k] }));
        setData(chartData);
      } else if (section === "customers") {
        const res = await api.get("/customers");
        // Count by company (simple example)
        const grouped = {};
        res.data.forEach((c) => (grouped[c.company || "Other"] = (grouped[c.company || "Other"] || 0) + 1));
        const chartData = Object.keys(grouped).map((k) => ({ name: k, count: grouped[k] }));
        setData(chartData);
      } else if (section === "invoices") {
        const res = await api.get("/invoices");
        // Count Paid vs Pending
        const grouped = {};
        res.data.forEach((i) => (grouped[i.status] = (grouped[i.status] || 0) + 1));
        const chartData = Object.keys(grouped).map((k) => ({ name: k, count: grouped[k] }));
        setData(chartData);
      }
    } catch (err) {
      console.error("Error fetching chart data:", err.response?.data || err.message);
    }
  };

  useEffect(() => {
    fetchStats();
    fetchChartData();
  }, [section]);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />

        {/* Top Stats */}
        <div className="p-6 grid grid-cols-3 gap-6">
          <div className="bg-white p-4 rounded shadow text-center">
            <p className="text-gray-500">Total Leads</p>
            <h2 className="text-2xl font-bold">{stats.totalLeads}</h2>
            <button onClick={() => setSection("leads")} className="text-blue-600 mt-2 underline">View</button>
          </div>
          <div className="bg-white p-4 rounded shadow text-center">
            <p className="text-gray-500">Total Customers</p>
            <h2 className="text-2xl font-bold">{stats.totalCustomers}</h2>
            <button onClick={() => setSection("customers")} className="text-blue-600 mt-2 underline">View</button>
          </div>
          <div className="bg-white p-4 rounded shadow text-center">
            <p className="text-gray-500">Total Invoices</p>
            <h2 className="text-2xl font-bold">{stats.totalInvoices}</h2>
            <button onClick={() => setSection("invoices")} className="text-blue-600 mt-2 underline">View</button>
          </div>
        </div>

        {/* Graph Section */}
        <div className="p-6 bg-white rounded shadow m-6 flex-1">
          <h2 className="text-xl font-bold mb-4 capitalize">{section} Statistics</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={data} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Legend />
              <Bar dataKey="count" fill="#3b82f6" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
