// UserDashboard.jsx
import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({ customers: 0, invoices: 0, leads: 0 });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customers = await api.get("/customers");
        const invoices = await api.get("/invoices");
        const leads = await api.get("/leads");
        setStats({
          customers: customers.data.length,
          invoices: invoices.data.length,
          leads: leads.data.length,
        });
      } catch (err) {
        console.error(err);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />
        <main className="p-6">
          <h1 className="text-2xl font-bold mb-6">Welcome, {user?.username}</h1>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white shadow rounded-lg p-6 border-t-4 border-blue-700">
              <h2 className="text-lg font-semibold">Customers</h2>
              <p className="text-3xl font-bold mt-2">{stats.customers}</p>
            </div>
            <div className="bg-white shadow rounded-lg p-6 border-t-4 border-teal-500">
              <h2 className="text-lg font-semibold">Invoices</h2>
              <p className="text-3xl font-bold mt-2">{stats.invoices}</p>
            </div>
            <div className="bg-white shadow rounded-lg p-6 border-t-4 border-yellow-500">
              <h2 className="text-lg font-semibold">Leads</h2>
              <p className="text-3xl font-bold mt-2">{stats.leads}</p>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
