import React, { useEffect, useState, useContext } from "react";
import { Link, useLocation, Navigate } from "react-router-dom";
import { LayoutDashboard, Users, FileText, Contact, Settings, LogOut } from "lucide-react";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const navItems = [
  { name: "Dashboard", path: "/", icon: <LayoutDashboard size={20} /> },
  { name: "Customers", path: "/customers", icon: <Users size={20} /> },
  { name: "Invoices", path: "/invoices", icon: <FileText size={20} /> },
  { name: "Leads", path: "/leads", icon: <Contact size={20} /> },
];

const Sidebar = () => {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);
  const [user, setUser] = useState(null);
  const { logout } = useContext(AuthContext);

  useEffect(() => {
    const loadUser = async () => {
      try {
        const res = await api.get("/auth/me");
        setUser(res.data);
      } catch (err) {
        console.error("Failed to fetch user:", err.response?.data || err.message);
      }
    };
    loadUser();
  }, []);

  if (!user) {
    return <Navigate to="/login" />;
  }

  return (
    <aside
      className={`h-screen bg-white/80 backdrop-blur-md shadow-sm flex flex-col transition-all duration-300 border-r
        ${collapsed ? "w-20" : "w-64"}
      `}
    >
      {/* Brand + Collapse Button */}
      <div className="flex items-center justify-between p-4 border-b">
        {!collapsed && <h1 className="text-xl font-bold text-indigo-700">Flow CRM</h1>}
        <button
          onClick={() => setCollapsed(!collapsed)}
          className="p-2 rounded hover:bg-gray-100 transition"
          title={collapsed ? "Expand" : "Collapse"}
        >
          {collapsed ? "→" : "←"}
        </button>
      </div>

      {/* User Info */}
      {!collapsed && user && (
        <div className="flex items-center gap-3 p-4 border-b">
          <img
            src={user.avatar || "/default-avatar.png"}
            alt="User"
            className="w-10 h-10 rounded-full border shadow-sm"
          />
          <div>
            <p className="font-medium">{user.name}</p>
            <p className="text-sm text-gray-500">{user.role || "Admin"}</p>
          </div>
        </div>
      )}

      {/* Navigation */}
      <nav className="flex-1 p-4 space-y-1 overflow-y-auto">
        {navItems.map((item) => {
          const active = location.pathname === item.path;
          return (
            <Link
              key={item.name}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-2 rounded-lg transition-all duration-200 relative
                ${active
                  ? "bg-indigo-50 text-indigo-700 font-medium shadow-sm"
                  : "hover:bg-gray-100 hover:text-indigo-600"}
                ${collapsed ? "justify-center" : ""}
              `}
            >
              {active && (
                <span className="absolute left-0 top-0 bottom-0 w-1 bg-indigo-600 rounded-r"></span>
              )}
              {item.icon}
              {!collapsed && <span>{item.name}</span>}
            </Link>
          );
        })}
      </nav>

      {/* Bottom */}
      <div className="p-4 border-t space-y-2">
        <Link
          to="/settings"
          className="flex items-center gap-3 px-4 py-2 rounded-lg hover:bg-gray-100 hover:text-indigo-600 transition"
        >
          <Settings size={20} />
          {!collapsed && <span>Settings</span>}
        </Link>

        <button
          onClick={logout}
          className="flex items-center gap-3 px-4 py-2 text-red-600 rounded-lg hover:bg-red-50 transition w-full"
        >
          <LogOut size={20} />
          {!collapsed && <span>Logout</span>}
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
