import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const customers = Array.isArray(customersRes.data.items)
          ? customersRes.data.items
          : customersRes.data;

        const invoicesRes = await api.get("/invoices");
        const invoices = Array.isArray(invoicesRes.data.items)
          ? invoicesRes.data.items
          : invoicesRes.data;

        const leadsRes = await api.get("/leads");
        const leads = Array.isArray(leadsRes.data.items)
          ? leadsRes.data.items
          : leadsRes.data;

        const totalRevenue = invoices.reduce((sum, inv) => sum + (inv.amount || 0), 0);
        const paidInvoices = invoices.filter(inv => inv.status?.toLowerCase() === "paid").length;
        const pendingInvoices = invoices.filter(inv => inv.status?.toLowerCase() === "pending").length;
        const inProgressLeads = leads.filter(
          l => l.status?.toLowerCase() === "new" || l.status?.toLowerCase() === "in progress"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads,
          totalRevenue,
          paidInvoices,
          pendingInvoices,
        });
      } catch (err) {
        console.error("Dashboard fetch error:", err.response?.data || err.message);
      }
    };
    fetchStats();
  }, []);

  // Main stats cards
  const mainCards = [
    { label: "Customers", value: stats.customers, color: "bg-indigo-900", textColor: "text-white", size: "large" },
    { label: "Leads", value: stats.leads, color: "bg-blue-900", textColor: "text-white", size: "medium" },
    { label: "Leads In Progress", value: stats.inProgressLeads, color: "bg-purple-900", textColor: "text-white", size: "medium" },
  ];

  // Invoice & Revenue cards
  const invoiceCards = [
    { label: "Total Invoices", value: stats.invoices, color: "bg-gray-800", textColor: "text-white", size: "medium" },
    { label: "Paid Invoices", value: stats.paidInvoices, color: "bg-emerald-800", textColor: "text-white", size: "small" },
    { label: "Pending Invoices", value: stats.pendingInvoices, color: "bg-amber-800", textColor: "text-white", size: "small" },
    { label: "Total Revenue", value: `â‚¹${stats.totalRevenue}`, color: "bg-teal-800", textColor: "text-white", size: "large" },
  ];

  // Function to determine card height based on importance
  const getCardHeight = size => {
    switch (size) {
      case "large":
        return "h-48";
      case "medium":
        return "h-36";
      default:
        return "h-28";
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Navbar />

        <main className="p-6 space-y-8">
          <h2 className="text-3xl font-bold text-gray-800">Dashboard</h2>

          {/* Main Stats Section */}
          <section className="space-y-6">
            {mainCards.map(card => (
              <div
                key={card.label}
                className={`${card.color} ${card.textColor} rounded-lg shadow p-6 flex flex-col justify-between ${getCardHeight(card.size)}`}
              >
                <p className="text-lg font-medium">{card.label}</p>
                <p className="text-4xl font-bold mt-4">{card.value}</p>
              </div>
            ))}
          </section>

          {/* Invoice & Revenue Section */}
          <section className="space-y-6">
            <h3 className="text-2xl font-semibold text-gray-700">Invoices & Revenue</h3>
            {invoiceCards.map(card => (
              <div
                key={card.label}
                className={`${card.color} ${card.textColor} rounded-lg shadow p-6 flex flex-col justify-between ${getCardHeight(card.size)}`}
              >
                <p className="text-lg font-medium">{card.label}</p>
                <p className="text-3xl font-bold mt-2">{card.value}</p>
              </div>
            ))}
          </section>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
