import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const resCustomers = await api.get("/customers");
        const resInvoices = await api.get("/invoices");
        const resLeads = await api.get("/leads");

        setStats({
          customers: resCustomers.data.length,
          invoices: resInvoices.data.length,
          leads: resLeads.data.length,
          inProgressLeads: resLeads.data.filter(l => l.status === "In Progress").length,
        });
      } catch (err) {
        console.error("Error fetching stats:", err.response?.data || err.message);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        <Navbar />

        <main className="p-6 overflow-auto">
          <h1 className="text-2xl font-bold text-gray-800 mb-6">
            Welcome, {user.username}!
          </h1>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="bg-white rounded-lg shadow p-6 border-t-4 border-blue-600 hover:shadow-lg transition">
              <p className="text-gray-500 font-semibold">Customers</p>
              <p className="text-3xl font-bold text-blue-600">{stats.customers}</p>
            </div>

            <div className="bg-white rounded-lg shadow p-6 border-t-4 border-green-500 hover:shadow-lg transition">
              <p className="text-gray-500 font-semibold">Invoices</p>
              <p className="text-3xl font-bold text-green-500">{stats.invoices}</p>
            </div>

            <div className="bg-white rounded-lg shadow p-6 border-t-4 border-yellow-500 hover:shadow-lg transition">
              <p className="text-gray-500 font-semibold">Leads</p>
              <p className="text-3xl font-bold text-yellow-500">{stats.leads}</p>
            </div>

            <div className="bg-white rounded-lg shadow p-6 border-t-4 border-purple-600 hover:shadow-lg transition">
              <p className="text-gray-500 font-semibold">Leads In Progress</p>
              <p className="text-3xl font-bold text-purple-600">{stats.inProgressLeads}</p>
            </div>
          </div>

          {/* Quick Action Buttons */}
          <div className="mt-8 flex flex-wrap gap-4">
            <button className="bg-blue-600 text-white px-6 py-3 rounded-lg shadow hover:bg-blue-700 transition">
              Add Customer
            </button>
            <button className="bg-green-500 text-white px-6 py-3 rounded-lg shadow hover:bg-green-600 transition">
              Add Invoice
            </button>
            <button className="bg-yellow-500 text-white px-6 py-3 rounded-lg shadow hover:bg-yellow-600 transition">
              Add Lead
            </button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
