import { useContext } from "react";
import { AuthContext } from "../Context/AuthContext";
import { Bell, PlusCircle } from "lucide-react";
import { useNavigate } from "react-router-dom";

const Navbar = ({ title }) => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <header className="h-16 bg-white shadow-sm flex items-center justify-between px-6">
      {/* Left: Page title */}
      <h1 className="text-lg font-semibold text-teal-700 tracking-tight">
        {title || "Overview"}
      </h1>

      {/* Center: Search / Quick Action */}
      <div className="flex-1 flex justify-center">
        <div className="w-full max-w-md">
          <input
            type="text"
            placeholder="Search customers, invoices..."
            className="w-full px-4 py-2 text-sm bg-gray-100 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-300 transition"
          />
        </div>
      </div>

      {/* Right: Actions + User */}
      <div className="flex items-center gap-4">
        {/* Quick Add */}
        <button
          onClick={() => alert("Open quick create modal")}
          className="flex items-center gap-1 text-sm bg-teal-600 text-white px-3 py-1.5 rounded-lg hover:bg-teal-700 transition"
        >
          <PlusCircle size={18} />
          <span className="hidden md:inline">New</span>
        </button>

        {/* Notifications */}
        <button className="relative text-gray-600 hover:text-teal-600 transition">
          <Bell size={22} />
          <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full px-1">
            3
          </span>
        </button>

        {/* User Avatar + Logout */}
        {user ? (
          <div className="flex items-center gap-3">
            <img
              src={user.avatar || "/path/to/default-avatar.png"}
              alt="User Avatar"
              className="w-8 h-8 rounded-full"
            />
            <span className="hidden md:inline text-gray-700 font-medium">
              {user.name}
            </span>
            <button
              onClick={handleLogout}
              className="text-red-500 hover:text-red-600 transition text-sm font-medium"
            >
              Logout
            </button>
          </div>
        ) : (
          <button
            onClick={() => navigate("/login")}
            className="bg-teal-600 text-white px-3 py-1.5 rounded-lg hover:bg-teal-700 transition"
          >
            Login
          </button>
        )}
      </div>
    </header>
  );
};

export default Navbar;
