const Navbar = ({ title }) => {
  return (
    <header className="h-16 bg-white/80 backdrop-blur-md shadow-sm flex items-center justify-between px-6 border-b">
      
      {/* Left: Brand / Page Title */}
      <h1 className="text-xl font-semibold text-indigo-700">{title}</h1>
      
      {/* Center: Search */}
      <div className="flex-1 flex justify-center px-6">
        <input
          type="text"
          placeholder="Search customers, invoices..."
          className="w-full max-w-md px-4 py-2 rounded-full border border-gray-200 bg-gray-50 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
        />
      </div>

      {/* Right: Actions */}
      <div className="flex items-center gap-4">
        {/* Quick Add */}
        <button className="bg-indigo-600 text-white px-4 py-2 rounded-full shadow hover:bg-indigo-700 transition">
          + Add
        </button>

        {/* Notifications */}
        <button className="relative p-2 rounded-full hover:bg-gray-100 transition">
          <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
          ðŸ””
        </button>

        {/* Profile Avatar */}
        <img
          src="/path/to/avatar.jpg"
          alt="User"
          className="w-10 h-10 rounded-full border border-gray-300 shadow-sm cursor-pointer hover:scale-105 transition"
        />
      </div>
    </header>
  );
};
