import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    newLeads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    pendingRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const customers =
          customersRes.data.items ||
          customersRes.data.customers ||
          customersRes.data ||
          [];

        const invoicesRes = await api.get("/invoices");
        let invoices =
          invoicesRes.data.items ||
          invoicesRes.data.invoices ||
          invoicesRes.data ||
          [];

        if (user?.role === "user") {
          invoices = invoices.filter(
            (inv) =>
              inv.createdBy === user._id ||
              inv.userId === user._id ||
              inv.owner === user._id
          );
        }

        const leadsRes = await api.get("/leads");
        const leads =
          leadsRes.data.items ||
          leadsRes.data.leads ||
          leadsRes.data ||
          [];

        const paidInvoicesList = invoices.filter(
          (inv) =>
            inv.status?.toLowerCase() === "paid" ||
            inv.status?.toLowerCase() === "success"
        );
        const pendingInvoicesList = invoices.filter(
          (inv) =>
            inv.status?.toLowerCase() === "pending" ||
            inv.status?.toLowerCase() === "unpaid"
        );

        const totalRevenue = paidInvoicesList.reduce(
          (sum, inv) => sum + (Number(inv.amount || inv.total || 0)),
          0
        );
        const pendingRevenue = pendingInvoicesList.reduce(
          (sum, inv) => sum + (Number(inv.amount || inv.total || 0)),
          0
        );

        const convertedLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "converted"
        ).length;
        const newLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "new"
        ).length;
        const inProgressLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "in progress"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: convertedLeads,
          newLeads,
          inProgressLeads,
          totalRevenue,
          pendingRevenue,
          paidInvoices: paidInvoicesList.length,
          pendingInvoices: pendingInvoicesList.length,
        });
      } catch (err) {
        console.error(
          "Dashboard fetch error:",
          err.response?.data || err.message
        );
      }
    };

    fetchStats();
  }, [user]);

  const StatCard = ({ title, value, bgColor, textColor }) => (
    <div
      className={`flex-1 min-w-[240px] rounded-2xl shadow-lg p-8 ${bgColor} text-white`}
    >
      <p className="font-medium text-xl">{title}</p>
      <p className={`font-extrabold text-5xl mt-4 ${textColor}`}>{value}</p>
    </div>
  );

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Navbar />
        <main className="p-8 space-y-12">
          {/* ðŸ”¹ Top Section */}
          <section>
            <h2 className="text-3xl font-semibold text-gray-800 mb-6">
              Overview
            </h2>
            <div className="flex flex-wrap gap-8">
              <StatCard
                title="Customers"
                value={stats.customers}
                bgColor="bg-blue-700"
                textColor="text-white"
              />
              <StatCard
                title="Leads (Converted)"
                value={stats.leads}
                bgColor="bg-purple-700"
                textColor="text-white"
              />
              <StatCard
                title="New Leads"
                value={stats.newLeads}
                bgColor="bg-orange-600"
                textColor="text-white"
              />
              <StatCard
                title="Leads In Progress"
                value={stats.inProgressLeads}
                bgColor="bg-indigo-700"
                textColor="text-white"
              />
            </div>
          </section>

          {/* ðŸ”¹ Invoices & Revenue Section */}
          <section>
            <h2 className="text-3xl font-semibold text-gray-800 mb-6">
              Invoices & Revenue
            </h2>
            <div className="flex flex-wrap gap-8">
              <StatCard
                title="Total Invoices"
                value={stats.invoices}
                bgColor="bg-green-700"
                textColor="text-white"
              />
              <StatCard
                title="Total Revenue (Paid)"
                value={`â‚¹${stats.totalRevenue}`}
                bgColor="bg-teal-700"
                textColor="text-white"
              />
              <StatCard
                title="Pending Revenue"
                value={`â‚¹${stats.pendingRevenue}`}
                bgColor="bg-amber-600"
                textColor="text-white"
              />
              <StatCard
                title="Paid Invoices"
                value={stats.paidInvoices}
                bgColor="bg-emerald-600"
                textColor="text-white"
              />
              <StatCard
                title="Pending Invoices"
                value={stats.pendingInvoices}
                bgColor="bg-rose-600"
                textColor="text-white"
              />
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
