import React from "react";
import { Bell, Plus } from "lucide-react";

const Navbar = ({ title }) => {
  return (
    <header className="h-16 bg-white/90 backdrop-blur-md shadow-sm flex items-center justify-between px-6 border-b">
      
      {/* Left: Page Title with subtle design */}
      <div className="flex items-center gap-3">
        <div className="w-2 h-8 bg-teal-600 rounded-full"></div> {/* accent bar */}
        <h1 className="text-xl font-semibold text-teal-700">{title}</h1>
      </div>

      {/* Right: Actions */}
      <div className="flex items-center gap-4">
        {/* Quick Add */}
        <button className="flex items-center gap-2 bg-teal-600 text-white px-4 py-2 rounded-full shadow hover:bg-teal-700 transition-transform transform hover:scale-105">
          <Plus size={16} />
          Add
        </button>

        {/* Notifications */}
        <button className="relative p-2 rounded-full hover:bg-gray-100 transition-transform transform hover:scale-105">
          <Bell size={20} />
          <span className="absolute top-0 right-0 w-2 h-2 bg-red-500 rounded-full animate-pulse"></span>
        </button>
      </div>
    </header>
  );
};

export default Navbar;
