import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import api from "../api";

const EditInvoice = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({ customer: "", amount: "", status: "" });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    const loadInvoice = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await api.get(`/api/invoices/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const inv = res.data;
        setForm({
          customer: inv.customer || "",
          amount: inv.amount || "",
          status: inv.status || "",
        });
      } catch (err) {
        console.error("Load Invoice error:", err.response?.data || err.message);
        alert("Could not load the invoice");
        navigate("/invoices");
      } finally {
        setLoading(false);
      }
    };
    loadInvoice();
  }, [id, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    try {
      const token = localStorage.getItem("token");
      await api.put(`/api/invoices/${id}`, form, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Invoice Updated Successfully!");
      navigate("/invoices");
    } catch (err) {
      console.error("Update Invoice error:", err.response?.data || err.message);
      alert("Update Failed");
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="max-w-md mx-auto mt-10 bg-white p-8 rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Edit Invoice</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="customer"
          value={form.customer}
          onChange={handleChange}
          placeholder="Customer"
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          required
        />
        <input
          name="amount"
          value={form.amount}
          onChange={handleChange}
          placeholder="Amount"
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          required
        />
        <input
          name="status"
          value={form.status}
          onChange={handleChange}
          placeholder="Status"
          className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-400 outline-none"
          required
        />
        <button
          type="submit"
          disabled={saving}
          className="w-full p-3 rounded-lg bg-blue-600 text-white font-semibold hover:bg-blue-700 transition"
        >
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </form>
    </div>
  );
};

export default EditInvoice;
