import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const customers = customersRes.data.items || [];

        const invoicesRes = await api.get("/invoices");
        const invoices = invoicesRes.data.items || [];

        const leadsRes = await api.get("/leads");
        const leads = leadsRes.data.items || [];

        const totalRevenue = invoices.reduce(
          (sum, inv) => sum + (inv.amount || 0),
          0
        );
        const paidInvoices = invoices.filter(
          (inv) => inv.status === "Paid"
        ).length;
        const pendingInvoices = invoices.filter(
          (inv) => inv.status === "Pending"
        ).length;
        const inProgressLeads = leads.filter(
          (l) => l.status === "New"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads,
          totalRevenue,
          paidInvoices,
          pendingInvoices,
        });
      } catch (err) {
        console.error(
          "Dashboard fetch error:",
          err.response?.data || err.message
        );
      }
    };
    fetchStats();
  }, []);

  const cards = [
    { label: "Customers", value: stats.customers, color: "bg-blue-500" },
    { label: "Invoices", value: stats.invoices, color: "bg-green-500" },
    { label: "Leads", value: stats.leads, color: "bg-yellow-500" },
    { label: "Leads In Progress", value: stats.inProgressLeads, color: "bg-purple-500" },
    { label: "Total Revenue", value: `₹${stats.totalRevenue}`, color: "bg-teal-500" },
    { label: "Paid Invoices", value: stats.paidInvoices, color: "bg-emerald-500" },
    { label: "Pending Invoices", value: stats.pendingInvoices, color: "bg-rose-500" },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />
        <main className="p-6 overflow-auto">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Dashboard</h2>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {cards.map((card) => (
              <div
                key={card.label}
                className="bg-white rounded-xl shadow-md p-5 flex flex-col justify-between border border-gray-200 hover:shadow-lg transition"
              >
                <div className="flex items-center justify-between">
                  <p className="text-sm font-medium text-gray-500">{card.label}</p>
                  <span className={`${card.color} w-3 h-3 rounded-full`}></span>
                </div>
                <p className="text-3xl font-semibold text-gray-800 mt-3">
                  {card.value}
                </p>
              </div>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
