import { useState, useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import { AuthContext } from "../Context/AuthContext";

const Sidebar = () => {
  const location = useLocation();
  const { logout, user } = useContext(AuthContext);

  const navItems = [
    { name: "Dashboard", path: "/", roles: ["user","admin"] },
    { name: "Customers", path: "/customers", roles: ["user","admin"] },
    { name: "Invoices", path: "/invoices", roles: ["user","admin"] },
    { name: "Leads", path: "/leads", roles: ["user","admin"] },
  ];

  return (
    <aside className="w-60 bg-blue-900 text-white min-h-screen p-4">
      <h1 className="text-xl font-bold mb-6">CRM</h1>
      <nav className="flex flex-col gap-3">
        {navItems
          .filter(item => item.roles.includes(user?.role))
          .map(item => (
            <Link
              key={item.name}
              to={item.path}
              className={`p-2 rounded hover:bg-blue-700 ${
                location.pathname === item.path ? "bg-blue-800" : ""
              }`}
            >
              {item.name}
            </Link>
          ))}
      </nav>
      <button
        onClick={logout}
        className="mt-6 w-full bg-red-600 p-2 rounded hover:bg-red-500"
      >
        Logout
      </button>
    </aside>
  );
};

export default Sidebar;
