import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from "recharts";

const COLORS = ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6", "#f97316"];

const UserDashboard = () => {
  const { user } = useContext(AuthContext);

  const [metrics, setMetrics] = useState({
    totalLeads: 0,
    leadsInProgress: 0,
    totalCustomers: 0,
    totalInvoices: 0,
  });

  const [activeTab, setActiveTab] = useState("Leads");
  const [graphData, setGraphData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [rawData, setRawData] = useState({ leads: [], customers: [], invoices: [] });

  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        const resLeads = await api.get("/leads");
        const resCustomers = await api.get("/customers");
        const resInvoices = await api.get("/invoices");

        const leads = resLeads.data.items ?? resLeads.data ?? [];
        const customers = resCustomers.data.items ?? resCustomers.data ?? [];
        const invoices = resInvoices.data.items ?? resInvoices.data ?? [];

        setRawData({ leads, customers, invoices });

        setMetrics({
          totalLeads: leads.length,
          leadsInProgress: leads.filter(l => ["Contacted", "Qualified"].includes(l.status)).length,
          totalCustomers: customers.length,
          totalInvoices: invoices.length,
        });
      } catch (err) {
        console.error("Dashboard fetch error:", err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchDashboard();
  }, []);

  useEffect(() => {
    if (activeTab === "Leads") {
      const statusCounts = rawData.leads.reduce((acc, lead) => {
        acc[lead.status] = (acc[lead.status] || 0) + 1;
        return acc;
      }, {});
      setGraphData(Object.entries(statusCounts).map(([status, count]) => ({ name: status, value: count })));
    } else if (activeTab === "Customers") {
      const companyCounts = rawData.customers.reduce((acc, customer) => {
        const company = customer.company || "Unknown";
        acc[company] = (acc[company] || 0) + 1;
        return acc;
      }, {});
      setGraphData(Object.entries(companyCounts).map(([company, count]) => ({ name: company, value: count })));
    } else if (activeTab === "Invoices") {
      const statusCounts = rawData.invoices.reduce((acc, invoice) => {
        acc[invoice.status] = (acc[invoice.status] || 0) + 1;
        return acc;
      }, {});
      setGraphData(Object.entries(statusCounts).map(([status, count]) => ({ name: status, value: count })));
    }
  }, [activeTab, rawData]);

  if (loading) return <p className="text-center mt-10">Loading Dashboard...</p>;

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />

        <main className="p-6 overflow-y-auto">
          <h1 className="text-2xl font-bold mb-6">Welcome, {user?.username || "User"}</h1>

          {/* Metrics Cards */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div
              onClick={() => setActiveTab("Leads")}
              className={`cursor-pointer p-4 rounded shadow ${activeTab === "Leads" ? "bg-blue-100" : "bg-white"}`}
            >
              <p className="text-gray-500">Total Leads</p>
              <p className="text-2xl font-bold">{metrics.totalLeads}</p>
            </div>

            <div
              onClick={() => setActiveTab("Leads")}
              className={`cursor-pointer p-4 rounded shadow ${activeTab === "Leads" ? "bg-blue-100" : "bg-white"}`}
            >
              <p className="text-gray-500">Leads in Progress</p>
              <p className="text-2xl font-bold">{metrics.leadsInProgress}</p>
            </div>

            <div
              onClick={() => setActiveTab("Customers")}
              className={`cursor-pointer p-4 rounded shadow ${activeTab === "Customers" ? "bg-green-100" : "bg-white"}`}
            >
              <p className="text-gray-500">Total Customers</p>
              <p className="text-2xl font-bold">{metrics.totalCustomers}</p>
            </div>

            <div
              onClick={() => setActiveTab("Invoices")}
              className={`cursor-pointer p-4 rounded shadow ${activeTab === "Invoices" ? "bg-yellow-100" : "bg-white"}`}
            >
              <p className="text-gray-500">Total Invoices</p>
              <p className="text-2xl font-bold">{metrics.totalInvoices}</p>
            </div>
          </div>

          {/* Pie Chart */}
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-xl font-bold mb-4">{activeTab} Distribution</h2>
            {graphData.length === 0 ? (
              <p>No data to display</p>
            ) : (
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={graphData}
                    dataKey="value"
                    nameKey="name"
                    outerRadius={100}
                    fill="#3b82f6"
                    label
                  >
                    {graphData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
