import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);

  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Fetch customers
        const customersRes = await api.get("/customers");
        console.log("Customers Response:", customersRes.data);
        const customers = customersRes.data.items || customersRes.data || [];

        // Fetch invoices
        const invoicesRes = await api.get("/invoices");
        console.log("Invoices Response:", invoicesRes.data);
        const invoices = invoicesRes.data.items || invoicesRes.data || [];

        // Fetch leads
        const leadsRes = await api.get("/leads");
        console.log("Leads Response:", leadsRes.data);
        const leads = leadsRes.data.items || leadsRes.data || [];

        // Count in-progress leads
        const inProgressLeads = leads.filter((l) => l.status === "New").length;

        // Set stats
        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads,
        });
      } catch (err) {
        console.error("Error fetching stats:", err.response?.data || err.message);
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />
        <main className="p-6 overflow-auto">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Dashboard</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {[
              { label: "Customers", value: stats.customers, color: "blue" },
              { label: "Invoices", value: stats.invoices, color: "green" },
              { label: "Leads", value: stats.leads, color: "yellow" },
              { label: "Leads In Progress", value: stats.inProgressLeads, color: "purple" },
            ].map((card) => (
              <div
                key={card.label}
                className={`bg-white rounded-lg shadow p-6 border-l-4 border-${card.color}-500 hover:shadow-md transition`}
              >
                <p className="text-gray-500 font-medium">{card.label}</p>
                <p className={`text-3xl font-bold text-${card.color}-500 mt-2`}>
                  {card.value}
                </p>
              </div>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
