import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import api from "../api";

const EditInvoice = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    customer: "",
    amount: "",
    description: "",
    status: "Pending",
  });
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    const loadInvoiceAndCustomers = async () => {
      try {
        const token = localStorage.getItem("token");

        // Load invoice
        const invoiceRes = await api.get(`/invoices/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const invoice = invoiceRes.data;

        // Load customers
        const customersRes = await api.get("/customers", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const customersList = customersRes.data.items ?? customersRes.data ?? [];

        setFormData({
          customer: invoice.customer?._id ?? invoice.customer ?? "",
          amount: invoice.amount ?? "",
          description: invoice.description ?? "",
          status: invoice.status ?? "Pending",
        });

        setCustomers(customersList);
      } catch (err) {
        console.error("Error loading invoice or customers:", err.response?.data || err.message);
        alert("Failed to load invoice or customers");
        navigate("/invoices");
      } finally {
        setLoading(false);
      }
    };

    loadInvoiceAndCustomers();
  }, [id, navigate]);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setSaving(true);
      const token = localStorage.getItem("token");
      await api.put(`/invoices/${id}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Invoice updated successfully");
      navigate("/invoices");
    } catch (err) {
      console.error("Error updating invoice:", err.response?.data || err.message);
      alert("Update failed");
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <p className="text-center mt-8">Loading invoice...</p>;

  return (
    <div className="bg-white shadow-lg rounded-lg max-w-md mx-auto p-6 mt-10">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Edit Invoice</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Customer</label>
          <select
            name="customer"
            value={formData.customer}
            onChange={handleChange}
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="">-- Select Customer --</option>
            {customers.map((c) => (
              <option key={c._id} value={c._id}>
                {c.name} — {c.company ?? ""}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Amount</label>
          <input
            type="number"
            name="amount"
            step="0.01"
            value={formData.amount}
            onChange={handleChange}
            placeholder="Amount"
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Description</label>
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Description (optional)"
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-600 mb-1">Status</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="Pending">Pending</option>
            <option value="Paid">Paid</option>
            <option value="Cancelled">Cancelled</option>
          </select>
        </div>

        <div className="flex gap-3 mt-4">
          <button
            type="submit"
            disabled={saving}
            className="flex-1 bg-green-600 text-white py-2 rounded hover:bg-green-700 transition"
          >
            {saving ? "Saving..." : "Save Changes"}
          </button>
          <button
            type="button"
            onClick={() => navigate("/invoices")}
            className="flex-1 bg-gray-300 text-gray-800 py-2 rounded hover:bg-gray-400 transition"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditInvoice;
