import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../Context/AuthContext";
import api from "../api"; // Axios instance
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);

  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    leadsInProgress: 0,
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [custRes, invRes, leadsRes] = await Promise.all([
          api.get("/customers"),
          api.get("/invoices"),
          api.get("/leads"),
        ]);

        const customers = custRes.data.items?.length ?? custRes.data.length ?? 0;
        const invoices = invRes.data.items?.length ?? invRes.data.length ?? 0;
        const leads = leadsRes.data.items?.length ?? leadsRes.data.length ?? 0;

        const leadsInProgress = leadsRes.data.items
          ? leadsRes.data.items.filter(l => l.status !== "Converted" && l.status !== "Lost").length
          : leadsRes.data.filter(l => l.status !== "Converted" && l.status !== "Lost").length;

        setStats({ customers, invoices, leads, leadsInProgress });
      } catch (err) {
        console.error("Error fetching dashboard stats:", err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading dashboard...</p>;

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />

        <main className="p-6">
          <h1 className="text-2xl font-bold mb-6">Welcome, {user.username}</h1>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="bg-white shadow rounded-lg p-4">
              <p className="text-sm text-gray-500">Total Customers</p>
              <p className="text-2xl font-bold">{stats.customers}</p>
            </div>

            <div className="bg-white shadow rounded-lg p-4">
              <p className="text-sm text-gray-500">Total Invoices</p>
              <p className="text-2xl font-bold">{stats.invoices}</p>
            </div>

            <div className="bg-white shadow rounded-lg p-4">
              <p className="text-sm text-gray-500">Total Leads</p>
              <p className="text-2xl font-bold">{stats.leads}</p>
            </div>

            <div className="bg-white shadow rounded-lg p-4">
              <p className="text-sm text-gray-500">Leads In Progress</p>
              <p className="text-2xl font-bold">{stats.leadsInProgress}</p>
            </div>
          </div>

          <section className="mt-10">
            <h2 className="text-xl font-semibold mb-4">Quick Actions</h2>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <button
                onClick={() => window.location.href = "/customers/add"}
                className="bg-green-500 text-white p-4 rounded shadow hover:bg-green-600"
              >
                Add Customer
              </button>
              <button
                onClick={() => window.location.href = "/invoices/add"}
                className="bg-blue-500 text-white p-4 rounded shadow hover:bg-blue-600"
              >
                Add Invoice
              </button>
              <button
                onClick={() => window.location.href = "/leads/add"}
                className="bg-yellow-500 text-white p-4 rounded shadow hover:bg-yellow-600"
              >
                Add Lead
              </button>
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
