import { useState,  useContext} from "react";
import { AuthContext } from "../Context/AuthContext";
import { LayoutDashboard, FileText, Contact, UserCog } from "lucide-react";
import {useLocation, Link } from "react-router-dom";
import { LogOut } from "lucide-react";
import { Users } from "lucide-react";

const Sidebar = () => {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);
  const { user, logout } = useContext(AuthContext);

  const navItems = [
    { name: "Dashboard", path: "/", icon: <LayoutDashboard size={20} />, roles: ["admin"] },
    { name: "Customers", path: "/customers", icon: <Users size={20} />, roles: ["admin","user"] },
    { name: "Invoices", path: "/invoices", icon: <FileText size={20} />, roles: ["admin","user"] },
    { name: "Leads", path: "/leads", icon: <Contact size={20} />, roles: ["admin","user"] },
    { name: "Users", path: "/users", icon: <UserCog size={20} />, roles: ["admin"] }
  ];

  return (
    <aside className={`h-screen ...`}>
      {/* Brand + Collapse */}
      <div className="flex items-center justify-between p-4 border-b">...</div>

      {/* Navigation */}
      <nav className="flex-1 p-4 space-y-1 overflow-y-auto">
        {navItems.filter(item => item.roles.includes(user?.role)).map(item => {
          const active = location.pathname === item.path;
          return (
            <Link key={item.name} to={item.path} className={`...`}>
              {item.icon}
              {!collapsed && <span>{item.name}</span>}
            </Link>
          );
        })}

        {/* Logout */}
        <div className="p-4 border-t">
          <button onClick={logout} className="flex items-center gap-3 px-4 py-2 text-red-600 rounded-lg hover:bg-red-50 transition w-full">
            <LogOut size={20} />
            {!collapsed && <span>Logout</span>}
          </button>
        </div>
      </nav>
    </aside>
  );
};
export default Sidebar
