import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api"; // Axios instance with token config
import { AuthContext } from "../Context/AuthContext";
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);

  const [metrics, setMetrics] = useState({
    totalLeads: 0,
    leadsInProgress: 0,
    totalCustomers: 0,
    totalInvoices: 0
  });

  const [graphData, setGraphData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        const resLeads = await api.get("/leads");
        const resCustomers = await api.get("/customers");
        const resInvoices = await api.get("/invoices");

        const leads = resLeads.data.items ?? resLeads.data ?? [];
        const customers = resCustomers.data.items ?? resCustomers.data ?? [];
        const invoices = resInvoices.data.items ?? resInvoices.data ?? [];

        setMetrics({
          totalLeads: leads.length,
          leadsInProgress: leads.filter(l => l.status === "Contacted" || l.status === "Qualified").length,
          totalCustomers: customers.length,
          totalInvoices: invoices.length
        });

        // Example graph: leads per status
        const statusCounts = leads.reduce((acc, lead) => {
          acc[lead.status] = (acc[lead.status] || 0) + 1;
          return acc;
        }, {});

        const graphArray = Object.entries(statusCounts).map(([status, count]) => ({
          status,
          count
        }));

        setGraphData(graphArray);

      } catch (err) {
        console.error("Dashboard fetch error: ", err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboard();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading Dashboard...</p>;

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <Navbar />

        {/* Main Content */}
        <main className="p-6 overflow-y-auto">
          <h1 className="text-2xl font-bold mb-6">Welcome, {user?.username || "User"}</h1>

          {/* Metrics Cards */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white p-4 rounded shadow">
              <p className="text-gray-500">Total Leads</p>
              <p className="text-2xl font-bold">{metrics.totalLeads}</p>
            </div>
            <div className="bg-white p-4 rounded shadow">
              <p className="text-gray-500">Leads in Progress</p>
              <p className="text-2xl font-bold">{metrics.leadsInProgress}</p>
            </div>
            <div className="bg-white p-4 rounded shadow">
              <p className="text-gray-500">Total Customers</p>
              <p className="text-2xl font-bold">{metrics.totalCustomers}</p>
            </div>
            <div className="bg-white p-4 rounded shadow">
              <p className="text-gray-500">Total Invoices</p>
              <p className="text-2xl font-bold">{metrics.totalInvoices}</p>
            </div>
          </div>

          {/* Leads Graph */}
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Leads Status Graph</h2>
            {graphData.length === 0 ? (
              <p>No data to display</p>
            ) : (
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={graphData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                  <XAxis dataKey="status" />
                  <YAxis />
                  <Tooltip />
                  <Line type="monotone" dataKey="count" stroke="#3b82f6" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
