import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { LayoutDashboard, Users, FileText, Contact, Settings, LogOut, UserCog, SlidersHorizontal } from "lucide-react";
import api from "../api";  // ✅ axios instance with baseURL + token
import { Navigate } from "react-router-dom"

const navItems = [
  { name: "Dashboard", path: "/", icon: <LayoutDashboard size={20} /> },
  { name: "Customers", path: "/customers", icon: <Users size={20} /> },
  { name: "Invoices", path: "/invoices", icon: <FileText size={20} /> },
  { name: "Leads", path: "/leads", icon: <Contact size={20} /> },
];

const Sidebar = () => {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);
  const [user, setUser] = useState(null);
  
  useEffect(() => {
    const loadUser = async () => {
      try {
        const res = await api.get("/auth/me"); // ✅ endpoint to get current logged in user
        setUser(res.data);
      } catch (err) {
        console.error("Failed to fetch user:", err.response?.data || err.message);
      }
    };
    loadUser();
  }, []);

  return (
    <aside className={`h-screen bg-white shadow-sm flex flex-col justify-between transition-all duration-300 ${collapsed ? 'w-20' : 'w-64'}`}>

      {/* Brand + Collapse Button */}
      <div className="flex items-center justifiy-between p-4 border-b">
        {!collapsed && <h1 className="text-xl font-bold text-teal-700">Flow CRM</h1>}
        <button onClick={()=> setCollapsed(!collapsed)} className="p-2 rounded hover:bg-gray-100 transition">
          {collapsed ? '→' : '←'}
        </button>
      </div>

      {/* Navigation */}
      <nav className="flex-1 p-4 space-y-1 overflow-y-auto">
        {
          navItems.map(item =>{
            const active = location.pathname === item.path
            return(
              <Link key={item.name} to={item.path} className={`flex items-center gap-3 px-4 py-2 rounded-lg transition-all duration-200
                ${active
                  ? "bg-teal-50 text-teal-700 font-medium shadow-sm"
                  : "hover:bg-gray-100 hover:text-teal-600"}
                ${collapsed ? 'justify-center' : ''}
              `}>
                {item.icon}
                {!collapsed && <span>{item.name}</span>}
              </Link>
            )
          })
        }

        {/* Bottom Links */}
        <div className="p-4 space-y-1 border-t">
          <Link to="/logout" className="flex items-center gap-3 px-4 py-2 text-red-600 rounded-lg hover:bg-red-50 transition">
            <LogOut size={20}/>
            {!collapsed && <span>Logout</span>}
          </Link>
        </div>
      </nav>
    </aside>
  );
};

export default Sidebar;
