import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    newLeads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    pendingRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const customers =
          customersRes.data.items ||
          customersRes.data.customers ||
          customersRes.data ||
          [];

        const invoicesRes = await api.get("/invoices");
        let invoices =
          invoicesRes.data.items ||
          invoicesRes.data.invoices ||
          invoicesRes.data ||
          [];

        if (user?.role === "user") {
          invoices = invoices.filter(
            (inv) =>
              inv.createdBy === user._id ||
              inv.userId === user._id ||
              inv.owner === user._id
          );
        }

        const leadsRes = await api.get("/leads");
        const leads =
          leadsRes.data.items ||
          leadsRes.data.leads ||
          leadsRes.data ||
          [];

        const paidInvoicesList = invoices.filter(
          (inv) =>
            inv.status?.toLowerCase() === "paid" ||
            inv.status?.toLowerCase() === "success"
        );
        const pendingInvoicesList = invoices.filter(
          (inv) =>
            inv.status?.toLowerCase() === "pending" ||
            inv.status?.toLowerCase() === "unpaid"
        );

        const totalRevenue = paidInvoicesList.reduce(
          (sum, inv) => sum + (Number(inv.amount || inv.total || 0)),
          0
        );
        const pendingRevenue = pendingInvoicesList.reduce(
          (sum, inv) => sum + (Number(inv.amount || inv.total || 0)),
          0
        );

        const convertedLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "converted"
        ).length;
        const newLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "new"
        ).length;
        const inProgressLeads = leads.filter(
          (l) => l.status?.toLowerCase() === "in progress"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: convertedLeads,
          newLeads,
          inProgressLeads,
          totalRevenue,
          pendingRevenue,
          paidInvoices: paidInvoicesList.length,
          pendingInvoices: pendingInvoicesList.length,
        });
      } catch (err) {
        console.error(
          "Dashboard fetch error:",
          err.response?.data || err.message
        );
      }
    };

    fetchStats();
  }, [user]);

  // ðŸ”¹ Bigger Card Component
  const StatCard = ({ title, value, borderColor, textColor }) => (
    <div
      className={`flex-1 min-w-[280px] bg-white rounded-2xl shadow-lg p-10 border-t-8 ${borderColor}`}
    >
      <p className="text-gray-600 font-semibold text-xl">{title}</p>
      <p className={`text-5xl font-extrabold mt-4 ${textColor}`}>{value}</p>
    </div>
  );

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Navbar />
        <main className="p-8 space-y-14">
          {/* ðŸ”¹ Top Section */}
          <section>
            <h2 className="text-3xl font-bold text-gray-800 mb-8">
              Overview
            </h2>
            <div className="flex flex-wrap gap-8">
              <StatCard
                title="Customers"
                value={stats.customers}
                borderColor="border-blue-700"
                textColor="text-blue-800"
              />
              <StatCard
                title="Leads (Converted)"
                value={stats.leads}
                borderColor="border-purple-700"
                textColor="text-purple-800"
              />
              <StatCard
                title="New Leads"
                value={stats.newLeads}
                borderColor="border-orange-700"
                textColor="text-orange-800"
              />
              <StatCard
                title="Leads In Progress"
                value={stats.inProgressLeads}
                borderColor="border-indigo-700"
                textColor="text-indigo-800"
              />
            </div>
          </section>

          {/* ðŸ”¹ Invoices & Revenue Section */}
          <section>
            <h2 className="text-3xl font-bold text-gray-800 mb-8">
              Invoices & Revenue
            </h2>
            <div className="flex flex-wrap gap-8">
              <StatCard
                title="Total Invoices"
                value={stats.invoices}
                borderColor="border-green-700"
                textColor="text-green-800"
              />
              <StatCard
                title="Total Revenue (Paid)"
                value={`â‚¹${stats.totalRevenue}`}
                borderColor="border-teal-700"
                textColor="text-teal-800"
              />
              <StatCard
                title="Pending Revenue"
                value={`â‚¹${stats.pendingRevenue}`}
                borderColor="border-amber-700"
                textColor="text-amber-800"
              />
              <StatCard
                title="Paid Invoices"
                value={stats.paidInvoices}
                borderColor="border-emerald-700"
                textColor="text-emerald-800"
              />
              <StatCard
                title="Pending Invoices"
                value={stats.pendingInvoices}
                borderColor="border-rose-700"
                textColor="text-rose-800"
              />
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
