import MainLayout from "../Components/MainLayout";
import { useEffect, useState } from "react";
import api from "../api"; // axios instance with token
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

const UserDashboard = () => {
  const [stats, setStats] = useState({ customers: 0, invoices: 0, leads: 0 });
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const invoicesRes = await api.get("/invoices");
        const leadsRes = await api.get("/leads");

        setStats({
          customers: customersRes.data.length,
          invoices: invoicesRes.data.length,
          leads: leadsRes.data.length,
        });

        // Example chart: invoices & leads per month
        const months = Array.from({ length: 12 }, (_, i) => i + 1);
        const chart = months.map((m) => ({
          name: `M${m}`,
          invoices: invoicesRes.data.filter((inv) => new Date(inv.createdAt).getMonth() + 1 === m).length,
          leads: leadsRes.data.filter((l) => new Date(l.createdAt).getMonth() + 1 === m).length,
        }));
        setChartData(chart);
      } catch (err) {
        console.error("Dashboard load error:", err.response?.data || err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  if (loading) return <MainLayout><p>Loading dashboard...</p></MainLayout>;

  return (
    <MainLayout>
      <h1 className="text-3xl font-bold mb-6">User Dashboard</h1>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white shadow rounded-lg p-6">
          <p className="text-sm text-gray-500">Total Customers</p>
          <p className="text-2xl font-bold">{stats.customers}</p>
        </div>
        <div className="bg-white shadow rounded-lg p-6">
          <p className="text-sm text-gray-500">Total Invoices</p>
          <p className="text-2xl font-bold">{stats.invoices}</p>
        </div>
        <div className="bg-white shadow rounded-lg p-6">
          <p className="text-sm text-gray-500">Leads in Progress</p>
          <p className="text-2xl font-bold">{stats.leads}</p>
        </div>
      </div>

      {/* Chart */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Invoices & Leads Overview</h2>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={chartData}>
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="invoices" fill="#3b82f6" />
            <Bar dataKey="leads" fill="#10b981" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </MainLayout>
  );
};

export default UserDashboard;
