import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const customersRes = await api.get("/customers");
        const customers = Array.isArray(customersRes.data.items)
          ? customersRes.data.items
          : customersRes.data;

        const invoicesRes = await api.get("/invoices");
        const invoices = Array.isArray(invoicesRes.data.items)
          ? invoicesRes.data.items
          : invoicesRes.data;

        const leadsRes = await api.get("/leads");
        const leads = Array.isArray(leadsRes.data.items)
          ? leadsRes.data.items
          : leadsRes.data;

        const totalRevenue = invoices.reduce((sum, inv) => sum + (inv.amount || 0), 0);
        const paidInvoices = invoices.filter(inv => inv.status?.toLowerCase() === "paid").length;
        const pendingInvoices = invoices.filter(inv => inv.status?.toLowerCase() === "pending").length;
        const inProgressLeads = leads.filter(
          l => l.status?.toLowerCase() === "new" || l.status?.toLowerCase() === "in progress"
        ).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads,
          totalRevenue,
          paidInvoices,
          pendingInvoices,
        });
      } catch (err) {
        console.error("Dashboard fetch error:", err.response?.data || err.message);
      }
    };
    fetchStats();
  }, []);

  const mainCards = [
    { label: "Customers", value: stats.customers, color: "bg-indigo-900", textColor: "text-white" },
    { label: "Leads", value: stats.leads, color: "bg-blue-900", textColor: "text-white" },
    { label: "Leads In Progress", value: stats.inProgressLeads, color: "bg-purple-900", textColor: "text-white" },
  ];

  const invoiceCards = [
    { label: "Total Invoices", value: stats.invoices, color: "bg-gray-800", textColor: "text-white" },
    { label: "Paid Invoices", value: stats.paidInvoices, color: "bg-emerald-800", textColor: "text-white" },
    { label: "Pending Invoices", value: stats.pendingInvoices, color: "bg-amber-800", textColor: "text-white" },
    { label: "Total Revenue", value: `â‚¹${stats.totalRevenue}`, color: "bg-teal-800", textColor: "text-white" },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Navbar />

        <main className="p-6 space-y-6">
          <h2 className="text-3xl font-bold text-gray-800">Dashboard</h2>

          {/* Main Stats */}
          <div className="flex flex-wrap gap-6">
            {mainCards.map(card => (
              <div
                key={card.label}
                className={`${card.color} ${card.textColor} rounded-lg shadow p-6 flex-1 min-w-[250px] flex flex-col justify-between`}
              >
                <p className="text-lg font-medium">{card.label}</p>
                <p className="text-4xl font-bold mt-4">{card.value}</p>
              </div>
            ))}
          </div>

         {/* Invoice Stats */}
        <h3 className="text-2xl font-semibold text-gray-700 mt-8">Invoices & Revenue</h3>
        <div className="flex flex-col gap-6 mt-4">
         {/* Total Invoices - important */}
          <div className="bg-gray-800 text-white rounded-lg shadow p-6 flex flex-col justify-between w-full">
            <p className="text-lg font-medium">Total Invoices</p>
            <p className="text-4xl font-bold mt-4">{stats.invoices}</p>
          </div>

  {/* Paid & Pending Invoices - smaller side-by-side */}
  <div className="flex flex-wrap gap-6">
    <div className="bg-emerald-800 text-white rounded-lg shadow p-6 flex-1 min-w-[200px] flex flex-col justify-between">
      <p className="text-lg font-medium">Paid Invoices</p>
      <p className="text-3xl font-bold mt-2">{stats.paidInvoices}</p>
    </div>
    <div className="bg-amber-800 text-white rounded-lg shadow p-6 flex-1 min-w-[200px] flex flex-col justify-between">
      <p className="text-lg font-medium">Pending Invoices</p>
      <p className="text-3xl font-bold mt-2">{stats.pendingInvoices}</p>
    </div>
  </div>

  {/* Total Revenue - important */}
  <div className="bg-teal-800 text-white rounded-lg shadow p-6 flex flex-col justify-between w-full">
    <p className="text-lg font-medium">Total Revenue</p>
    <p className="text-4xl font-bold mt-4">â‚¹{stats.totalRevenue}</p>
  </div>
</div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
