import { useState } from "react"
import { useNavigate } from "react-router-dom"
import axios from "axios"

const AddLeads = ()=>{

    const navigate = useNavigate()
    const [formData, setformData] = useState({name: "", email: "", phone: "", status: ""})

    const handleChange = (e)=>{
        setformData({...formData, [e.target.name]:e.target.value})
    }

    const handleSubmit = async (e)=>{
        e.preventDefault()
        try{
            await axios.post("http://localhost:5000/api/leads", formData)
            navigate("/leads")
        }catch(err){
            console.error(err)
        }
    }

    return(
        <div className="bg-white shadow p-6 rounded-lg max-w-md mx-auto">
            <h1 className="text-xl font-bold mb-4">Add New Lead</h1>
            <form onSubmit={hand} className="space-y-4">
                <input type="text" placeholder="Name" name="name" className="w-full border p-2 rounded" required  />
                <input type="text" placeholder="Phone" name="phone" className="w-full border p-2 rounded" required  />
                <input type="text" placeholder="Email" name="email" className="w-full border p-2 rounded" required  />
                <input type="text" placeholder="Status" name="status" className="w-full border p-2 rounded" required  />
                <button type="submit" className="bg-green-500 text-white p-2 rounded">Add Lead</button>
            </form>
        </div>
    )
}
export default AddLeads
