import { useEffect, useState, useContext } from "react";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api";
import { AuthContext } from "../Context/AuthContext";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // ✅ fetch customers
        const customersRes = await api.get("/customers");
        const customers = customersRes.data.items || [];

        // ✅ fetch invoices
        const invoicesRes = await api.get("/invoices");
        const invoices = invoicesRes.data.items || [];

        // ✅ fetch leads
        const leadsRes = await api.get("/leads");
        const leads = leadsRes.data.items || [];

        // ✅ extra calculations
        const totalRevenue = invoices.reduce((sum, inv) => sum + (inv.amount || 0), 0);
        const paidInvoices = invoices.filter(inv => inv.status === "Paid").length;
        const pendingInvoices = invoices.filter(inv => inv.status === "Pending").length;
        const inProgressLeads = leads.filter(l => l.status === "").length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: leads.length,
          inProgressLeads,
          totalRevenue,
          paidInvoices,
          pendingInvoices,
        });
      } catch (err) {
        console.error("Dashboard fetch error:", err.response?.data || err.message);
      }
    };
    fetchStats();
  }, []);

  const cards = [
    { label: "Customers", value: stats.customers, color: "blue" },
    { label: "Invoices", value: stats.invoices, color: "green" },
    { label: "Leads", value: stats.leads, color: "yellow" },
    { label: "Leads In Progress", value: stats.inProgressLeads, color: "purple" },
    { label: "Total Revenue", value: `₹${stats.totalRevenue}`, color: "teal" },
    { label: "Paid Invoices", value: stats.paidInvoices, color: "emerald" },
    { label: "Pending Invoices", value: stats.pendingInvoices, color: "rose" },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Navbar />
        <main className="p-6 overflow-auto">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Dashboard</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {cards.map(card => (
              <div
                key={card.label}
                className={`bg-white rounded-lg shadow p-6 border-l-4 border-${card.color}-500 hover:shadow-md transition`}
              >
                <p className="text-gray-500 font-medium">{card.label}</p>
                <p className={`text-2xl font-bold text-${card.color}-600 mt-2`}>
                  {card.value}
                </p>
              </div>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
