import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import { Edit, Trash2 } from "lucide-react";
import Button from "../Components/Button";

const Customer = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();
  const API_URL = "http://localhost:5000/api/customers";

  useEffect(() => {
    const fetchCustomers = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await api.get(API_URL, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCustomers(response.data.items ?? response.data);
      } catch (error) {
        console.log("Error Fetching Customers: " + error);
      } finally {
        setLoading(false);
      }
    };
    fetchCustomers();
  }, []);

  const handleDelete = async (id) => {
    const ok = window.confirm("Delete the Customer? This cannot be undone");
    if (!ok) return;

    try {
      await api.delete(`customers/${id}`);
      setCustomers((prev) => prev.filter((c) => c._id !== id));
    } catch (err) {
      console.error("Delete error: ", err.response?.data || err.message);
      alert(err.response?.data?.error || "Delete Failed");
    }
  };

  if (loading) return <p className="text-gray-600">Loading Customers...</p>;

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold text-gray-800">Customers</h2>
        <Button variant="primary" onClick={() => navigate("/customers/add")}>
          Add Customer
        </Button>
      </div>

      {customers.length === 0 ? (
        <p className="text-gray-500">No Customers Found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 border border-gray-200 rounded-lg">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Email
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Phone
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Company
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {customers.map((cust, index) => (
                <tr
                  key={cust._id}
                  className={index % 2 === 0 ? "bg-gray-50" : "bg-white"}
                >
                  <td className="px-6 py-4 whitespace-nowrap">{cust.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{cust.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{cust.phone}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{cust.company}</td>
                  <td className="px-6 py-4 whitespace-nowrap flex gap-2">
                    <Button
                      variant="warning"
                      onClick={() => navigate(`/customers/edit/${cust._id}`)}
                    >
                      <Edit size={16} /> Edit
                    </Button>
                    <Button
                      variant="danger"
                      onClick={() => handleDelete(cust._id)}
                    >
                      <Trash2 size={16} /> Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Customer;
