import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    users: 0,
  });
  const [recentInvoices, setRecentInvoices] = useState([]);
  const [recentUsers, setRecentUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const loadData = async () => {
      try {
        const token = localStorage.getItem("token");
        const [customersRes, invoicesRes, leadsRes, usersRes] = await Promise.all([
          api.get("/customers", { headers: { Authorization: `Bearer ${token}` } }),
          api.get("/invoices", { headers: { Authorization: `Bearer ${token}` } }),
          api.get("/leads", { headers: { Authorization: `Bearer ${token}` } }),
          api.get("/users", { headers: { Authorization: `Bearer ${token}` } }),
        ]);

        setStats({
          customers: customersRes.data.items?.length ?? customersRes.data.length ?? 0,
          invoices: invoicesRes.data.length ?? 0,
          leads: leadsRes.data.length ?? 0,
          users: usersRes.data.length ?? 0,
        });

        setRecentInvoices(invoicesRes.data.slice(0, 5));
        setRecentUsers(usersRes.data.slice(0, 5));
      } catch (err) {
        console.error("Error loading admin dashboard:", err.response?.data || err.message);
      }
    };

    loadData();
  }, []);

  return (
    <div className="p-6">
      {/* Header */}
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="p-4 bg-blue-100 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Customers</h2>
          <p className="text-2xl font-bold">{stats.customers}</p>
        </div>
        <div className="p-4 bg-green-100 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Invoices</h2>
          <p className="text-2xl font-bold">{stats.invoices}</p>
        </div>
        <div className="p-4 bg-yellow-100 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Leads</h2>
          <p className="text-2xl font-bold">{stats.leads}</p>
        </div>
        <div className="p-4 bg-purple-100 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Users</h2>
          <p className="text-2xl font-bold">{stats.users}</p>
        </div>
      </div>

      {/* Management Section */}
      <h2 className="text-xl font-bold mb-4">Management</h2>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <button
          onClick={() => navigate("/customers")}
          className="p-4 bg-white shadow rounded-lg hover:bg-gray-50 text-center"
        >
          Manage Customers
        </button>
        <button
          onClick={() => navigate("/invoices")}
          className="p-4 bg-white shadow rounded-lg hover:bg-gray-50 text-center"
        >
          Manage Invoices
        </button>
        <button
          onClick={() => navigate("/leads")}
          className="p-4 bg-white shadow rounded-lg hover:bg-gray-50 text-center"
        >
          Manage Leads
        </button>
        <button
          onClick={() => navigate("/users")}
          className="p-4 bg-white shadow rounded-lg hover:bg-gray-50 text-center"
        >
          Manage Users
        </button>
      </div>

      {/* Reports */}
      <h2 className="text-xl font-bold mb-4">Reports</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        {/* Recent Invoices */}
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-3">Recent Invoices</h3>
          <table className="w-full text-sm">
            <thead>
              <tr className="border-b">
                <th className="p-2 text-left">Customer</th>
                <th className="p-2 text-left">Amount</th>
                <th className="p-2 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {recentInvoices.map((inv) => (
                <tr key={inv._id} className="border-b">
                  <td className="p-2">{inv.customer?.name ?? "Unknown"}</td>
                  <td className="p-2">â‚¹{inv.amount}</td>
                  <td className="p-2">{inv.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Recent Users */}
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-3">Recent Users</h3>
          <ul className="space-y-2">
            {recentUsers.map((u) => (
              <li key={u._id} className="border-b pb-2">
                <p className="font-semibold">{u.username}</p>
                <p className="text-sm text-gray-500">{u.email}</p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
