import { useEffect, useState, useContext } from "react";
import { AuthContext } from "../Context/AuthContext";
import { Link } from "react-router-dom";
import api from "../api";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    inProgressLeads: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [custRes, invRes, leadRes] = await Promise.all([
          api.get("/customers"),
          api.get("/invoices"),
          api.get("/leads"),
        ]);

        const leads = leadRes.data.items || [];
        const inProgress = leads.filter(
          (l) => l.status === "Contacted" || l.status === "Qualified"
        ).length;

        setStats({
          customers: custRes.data.items.length || 0,
          invoices: invRes.data.items.length || 0,
          leads: leads.length,
          inProgressLeads: inProgress,
        });
      } catch (err) {
        console.error("Error fetching dashboard stats:", err.response?.data || err.message);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-6">Welcome, {user?.username}</h1>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 mb-6">
        <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition cursor-pointer">
          <h2 className="text-lg font-semibold">Customers</h2>
          <p className="text-2xl font-bold mt-2">{stats.customers}</p>
          <Link to="/customers" className="text-blue-500 mt-3 block">View Customers</Link>
        </div>

        <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition cursor-pointer">
          <h2 className="text-lg font-semibold">Invoices</h2>
          <p className="text-2xl font-bold mt-2">{stats.invoices}</p>
          <Link to="/invoices" className="text-blue-500 mt-3 block">View Invoices</Link>
        </div>

        <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition cursor-pointer">
          <h2 className="text-lg font-semibold">Leads</h2>
          <p className="text-2xl font-bold mt-2">{stats.leads}</p>
          <Link to="/leads" className="text-blue-500 mt-3 block">View Leads</Link>
        </div>

        <div className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition cursor-pointer">
          <h2 className="text-lg font-semibold">Leads In Progress</h2>
          <p className="text-2xl font-bold mt-2">{stats.inProgressLeads}</p>
          <Link to="/leads" className="text-blue-500 mt-3 block">Manage Leads</Link>
        </div>
      </div>

      {/* Quick Links Section */}
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
          <Link
            to="/customers/add"
            className="bg-blue-500 text-white p-4 rounded text-center hover:bg-blue-600 transition"
          >
            Add Customer
          </Link>
          <Link
            to="/invoices/add"
            className="bg-green-500 text-white p-4 rounded text-center hover:bg-green-600 transition"
          >
            Add Invoice
          </Link>
          <Link
            to="/leads/add"
            className="bg-yellow-500 text-white p-4 rounded text-center hover:bg-yellow-600 transition"
          >
            Add Lead
          </Link>
          <Link
            to="/customers"
            className="bg-gray-500 text-white p-4 rounded text-center hover:bg-gray-600 transition"
          >
            Manage Customers
          </Link>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
