import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import Sidebar from "../Components/Sidebar";
import Navbar from "../Components/Navbar";
import api from "../api"; // your axios instance (if configured)
import { AuthContext } from "../Context/AuthContext";

const API_BASE = "http://localhost:5000/api"; // fallback base

// helper to normalize "array" responses
function extractArray(payload) {
  if (!payload) return [];
  if (Array.isArray(payload)) return payload;
  if (Array.isArray(payload.items)) return payload.items;
  if (Array.isArray(payload.invoices)) return payload.invoices;
  if (Array.isArray(payload.data)) return payload.data;
  return [];
}

// helper to attempt fetch via api, fallback to axios with token
async function safeGet(endpoint) {
  // try api (likely already has baseURL + token)
  try {
    const res = await api.get(endpoint);
    return res;
  } catch (err) {
    console.warn(`api.get(${endpoint}) failed:`, err?.response?.status || err.message);
    // fallback to axios with explicit Authorization header
    try {
      const token = localStorage.getItem("token");
      const url = endpoint.startsWith("/") ? `${API_BASE}${endpoint}` : `${API_BASE}/${endpoint}`;
      const res2 = await axios.get(url, {
        headers: token ? { Authorization: `Bearer ${token}` } : {}
      });
      return res2;
    } catch (err2) {
      // rethrow to be handled upstream
      throw err2;
    }
  }
}

const UserDashboard = () => {
  const { user, logout } = useContext(AuthContext); // logout available if token expired
  const [stats, setStats] = useState({
    customers: 0,
    invoices: 0,
    leads: 0,
    newLeads: 0,
    inProgressLeads: 0,
    totalRevenue: 0,
    paidInvoices: 0,
    pendingInvoices: 0,
  });
  const [loading, setLoading] = useState(true);
  const [fetchError, setFetchError] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      setLoading(true);
      setFetchError(null);

      try {
        // ---------- customers ----------
        const customersRes = await safeGet("/customers");
        console.log("customersRes.data:", customersRes?.data);
        const customers = extractArray(customersRes?.data);

        // ---------- invoices ----------
        const invoicesRes = await safeGet("/invoices");
        console.log("invoicesRes.data:", invoicesRes?.data);
        let invoices = extractArray(invoicesRes?.data);

        // If the API responded with an object (not array) that contains `.items`, extractArray already handled it.

        // ---------- leads ----------
        const leadsRes = await safeGet("/leads");
        console.log("leadsRes.data:", leadsRes?.data);
        const leads = extractArray(leadsRes?.data);

        // If user is a normal user, filter invoices by ownership.
        // Support multiple possible fields and id shapes.
        const currentUserId = user?.id ?? user?._id ?? null;
        if (user?.role === "user" && currentUserId) {
          invoices = invoices.filter((inv) => {
            // possible ownership fields
            const owners = [
              inv.createdBy,
              inv.userId,
              inv.owner,
              inv.created_by, // just in case
              inv.user, // sometimes nested user object
            ];

            // if inv.user is an object with _id or id
            if (inv.user && typeof inv.user === "object") {
              owners.push(inv.user._id || inv.user.id);
            }

            // normalize to string compare
            return owners.some(o => o && String(o) === String(currentUserId));
          });
        }

        // ---------- calculations ----------
        const totalRevenue = invoices.reduce((sum, inv) => sum + (Number(inv.amount) || 0), 0);
        const paidInvoices = invoices.filter(inv => String(inv.status ?? "").toLowerCase() === "paid").length;
        const pendingInvoices = invoices.filter(inv => String(inv.status ?? "").toLowerCase() === "pending").length;

        const convertedLeads = leads.filter(l => String(l.status ?? "").toLowerCase() === "converted").length;
        const newLeads = leads.filter(l => String(l.status ?? "").toLowerCase() === "new").length;
        const inProgressLeads = leads.filter(l => {
          const s = String(l.status ?? "").toLowerCase();
          return s === "in progress" || s === "contacted"; // treat "contacted" as in-progress optionally
        }).length;

        setStats({
          customers: customers.length,
          invoices: invoices.length,
          leads: convertedLeads,
          newLeads,
          inProgressLeads,
          totalRevenue,
          paidInvoices,
          pendingInvoices,
        });

      } catch (err) {
        console.error("Dashboard fetch error:", err?.response?.data || err.message);
        // If unauthorized, suggest logout
        if (err?.response?.status === 401) {
          setFetchError("Unauthorized: token may be invalid or expired. Logging out.");
          // optionally logout automatically:
          // logout();
        } else {
          setFetchError(err?.response?.data?.message || err.message || "Failed to fetch dashboard data");
        }
        // keep stats as zeros
      } finally {
        setLoading(false);
      }
    };

    // Start fetch only when user context is set (so currentUserId available)
    // If user is null, still fetch (admin may see all). We still run; the fetch will use token fallback.
    fetchStats();
  }, [user, logout]);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Navbar />
        <main className="p-6">
          {loading ? (
            <div className="p-6 bg-white rounded shadow text-center">Loading dashboard...</div>
          ) : fetchError ? (
            <div className="p-4 bg-red-50 border border-red-200 text-red-700 rounded mb-4">
              <strong>Error:</strong> {fetchError}
            </div>
          ) : null}

          {/* Top Section */}
          <div className="flex flex-col md:flex-row md:flex-wrap gap-6 mb-8">
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-blue-600">
              <p className="text-gray-500 font-medium text-lg">Customers</p>
              <p className="text-3xl font-bold text-blue-700 mt-2">{stats.customers}</p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-purple-600">
              <p className="text-gray-500 font-medium text-lg">Leads (Converted)</p>
              <p className="text-3xl font-bold text-purple-700 mt-2">{stats.leads}</p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-orange-600">
              <p className="text-gray-500 font-medium text-lg">New Leads</p>
              <p className="text-3xl font-bold text-orange-700 mt-2">{stats.newLeads}</p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-indigo-600">
              <p className="text-gray-500 font-medium text-lg">Leads In Progress</p>
              <p className="text-3xl font-bold text-indigo-700 mt-2">{stats.inProgressLeads}</p>
            </div>
          </div>

          {/* Invoices & Revenue Section */}
          <div className="flex flex-col md:flex-row md:flex-wrap gap-6">
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-green-600">
              <p className="text-gray-500 font-medium text-lg">Invoices</p>
              <p className="text-3xl font-bold text-green-700 mt-2">{stats.invoices}</p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-teal-600">
              <p className="text-gray-500 font-medium text-lg">Total Revenue</p>
              <p className="text-3xl font-bold text-teal-700 mt-2">â‚¹{stats.totalRevenue}</p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-emerald-600">
              <p className="text-gray-500 font-medium text-lg">Paid Invoices</p>
              <p className="text-3xl font-bold text-emerald-700 mt-2">{stats.paidInvoices}</p>
            </div>
            <div className="flex-1 bg-white rounded-lg shadow p-6 border-l-4 border-rose-600">
              <p className="text-gray-500 font-medium text-lg">Pending Invoices</p>
              <p className="text-3xl font-bold text-rose-700 mt-2">{stats.pendingInvoices}</p>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
